function menu_OnFocus(obj)
{
	gFcOnTopButtonColor("2.8"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)
}

function cmb_ORDAT_OnKillFocus(obj)
{
	if(length(trim(obj.Value)) < 1) return;

	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getSC003PR";		
	var sMethodName = "service::pscomlib/selectSC003PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SC003PR=ds_SC003PR";
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " PRMNO=" + quote("ABS"); //사업부
		sArgument += " YRMON=" + quote(substr(obj.Value,0,6)); //계획년월
		sArgument += " YRMONDT=" + quote(substr(obj.Value,6,2)); //계획일
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_SC003PR.GetRowCount() < 1){
		//alert("[알림]일자를 확인하세요.");
		alert(setPopMessage("6029"));//[알림]일자를 확인하세요.
		setMessage("6029");	
		cmb_ORDAT.SetFocus();
		return;
	}
	http.usewaitcursor = true;
}

function ds_FGPZ149_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "PRMANS" || strColumnID == "QCMANS"){
		if(varNewValue!=""){
			if(length(ds_FGPZ149.GetColumn(nRow,"ORDAT"))==0){
				ds_FGPZ149.SetColumn(nRow, "COMPS", ComboBoxPlant.Value); // 사업부 설정
				ds_FGPZ149.SetColumn(nRow, "ORDAT", cmb_ORDAT.Value); // 지시일자 설정
				if(length(ds_FGPZ149.GetColumn(nRow,"DAYGU"))==0){
					ds_FGPZ149.SetColumn(nRow, "DAYGU", cmb_DAYGU.Value); // 주야구분 설정
				}
			}
			if(length(ds_FGPZ149.GetColumn(nRow,"PRMANS"))==0){
				ds_FGPZ149.SetColumn(nRow, "PRMANS", 0); // 주야구분 설정
			}
			if(length(ds_FGPZ149.GetColumn(nRow,"QCMANS"))==0){
				ds_FGPZ149.SetColumn(nRow, "QCMANS", 0); // 주야구분 설정
			}
		}
	}	
}
function cmb_DAYGU_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		fn_Search();
	}
}
