function fn_Input()
{
	if(!validationChk()){
		return;
	}
	if(checkInsertCount("ds_FGPZ160")== false) return;
	if(ds_FGPZ160.rowcount < 1) fn_Search();
	ds_FGPZ160.AddRow();
	ds_FGPZ160.SetColumn(ds_FGPZ160.currow,"PINBR",ed_PINBR.Text);
	ds_FGPZ160.SetColumn(ds_FGPZ160.currow,"CHKSQ",ds_FGPZ160.row+1);
}

function fn_Delete()
{
	if(ds_FGPZ160.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_FGPZ160.DeleteRow(ds_FGPZ160.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
	setMessage("1011");
}

