function getCGMPARMAA2(itnbr){

	var rlt = true;
	var sKind		= "getCGMPARMAA2";		
	var sMethodName = "service::pscomlib/selectCGMPARMAA2.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "";
	var sOutDataSet = "ds_CGMPARMAA=ds_CGMPARMAA";
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " PSPNO=" + quote(itnbr); //작업공정
		//sArgument += " ITTYP=" + quote(1); //제품,내제품만 등록가능함.
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	if (ds_CGMPARMAA.GetRowCount() < 1){
		//alert("[알림]ITEM MASTER A에 미등록된 품번입니다.");
		alert(setPopMessage("6032"));//[알림]ITEM MASTER A에 미등록된 품번입니다.
		setMessage("6032");	
		rlt =false;
	}else{
		//12112-00424
		var ittyp=ds_CGMPARMAA.GetColumn(0,"ITTYP");
		var itc=ds_CGMPARMAA.GetColumn(0,"ITC");
		if(ittyp=="1" && itc=="1"){
			//alert("[알림]제품 및 A-PART는 등록될 수 없습니다.");
			alert(setPopMessage("6031"));//[알림]제품 및 A-PART는 등록될 수 없습니다.
			setMessage("6031");	
			rlt =false;		
		}
		//11000-602210B
		else if( ittyp=="9"){
			//alert("[알림]제품 및 A-PART는 등록될 수 없습니다.");
			alert(setPopMessage("6031"));//[알림]제품 및 A-PART는 등록될 수 없습니다.
			setMessage("6031");
			rlt =false;		
		}
	}
	return rlt;
}

function ds_FGPZ160_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "CINBR"){//형태확인=> 제품 및 A-PART는 등록될 수 없음.
		if(varNewValue!=""){
			if(ds_FGPZ160.FindRow("CINBR",varNewValue)!="-1"){
				//alert("[경고]이미등록된 자품번입니다.");
				alert(setPopMessage("6033"));//[경고]이미등록된 자품번입니다.
				setMessage("6033");
				if(ds_FGPZ160.GetRowType(nRow)=="insert"){
					ds_FGPZ160.DeleteRow(nRow);
					ds_FGPZ160.SelectRow(ds_FGPZ160.FindRow("CINBR",varNewValue));				
				}else{
					ds_FGPZ160.SelectRow(nRow, false);
					ds_FGPZ160.SelectRow(ds_FGPZ160.FindRow("CINBR",varNewValue));				
				}
				return false;
			}
			var setCINBR = getCGMPARMAA2(varNewValue);
			if(setCINBR == false){
				return setCINBR;
			}
		}
	}
}

function ds_FGPZ160_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "CINBR"){//형태확인=> 제품 및 A-PART는 등록될 수 없음.
		if(varNewValue!=""){
			ds_FGPZ160.SetColumn(ds_FGPZ160.currow,"ITDSC",ds_CGMPARMAA.GetColumn(0,"ITDSC"));
			ds_FGPZ160.SetColumn(ds_FGPZ160.currow,"ITTYP",ds_CGMPARMAA.GetColumn(0,"ITTYP"));
			ds_FGPZ160.SetColumn(ds_FGPZ160.currow,"ITCLS",ds_CGMPARMAA.GetColumn(0,"ITCLS"));
			ds_FGPZ160.SetColumn(ds_FGPZ160.currow,"UNMSR",ds_CGMPARMAA.GetColumn(0,"UNMSR"));
		}else{
			ds_FGPZ160.SetColumn(ds_FGPZ160.currow,"ITDSC","");
			ds_FGPZ160.SetColumn(ds_FGPZ160.currow,"ITTYP","");
			ds_FGPZ160.SetColumn(ds_FGPZ160.currow,"ITCLS","");
			ds_FGPZ160.SetColumn(ds_FGPZ160.currow,"UNMSR","");
		}
	}	
}
function ed_PINBR_OnKillFocus(obj)
{
	if(length(trim(obj.Value)) < 1) return;
	if(length(trim(st_PINBR.Text)) > 1) return;
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getCGMPARMAA2";		
	var sMethodName = "service::pscomlib/selectCGMPARMAA2.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "";
	var sOutDataSet = "ds_CGMPARMAA=ds_CGMPARMAA";
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " PSPNO=" + quote(obj.Value); //작업공정
		sArgument += " ITTYP=" + quote(1); //제품,내제품만 등록가능함.
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_CGMPARMAA.GetRowCount() < 1){
		//alert("[알림]생산품이 아닙니다. 품번을 확인하세요.");
		alert(setPopMessage("6030"));//[알림]생산품이 아닙니다. 품번을 확인하세요.
		setMessage("6030");	
		ed_PINBR.SetFocus();
		return;
	}
	st_PINBR.Text=ds_CGMPARMAA.GetColumn(0,"ITDSC");
	http.usewaitcursor = true;			
}

function ed_PINBR_OnCharChanged(obj,strPreText,strPostText)
{
	st_PINBR.Text = "";
}

function ed_PINBR_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		if(length(trim(st_PINBR.Text)) < 1){
			ed_PINBR_OnKillFocus(obj);
		}
		fn_Search();
	}
}
