	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	//MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getFGPZ170(){

	if(!validationChk()){
		return;
	}

	var sKind		= "getFGPZ170";						
	var sMethodName = "service::psfaclib/selectFGPZ170.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FGPZ170=ds_FGPZ170";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@WRKYMK=" + quote(substr(cmb_WRKYMK.Value,0,6)); //계획년월
		sArgument += "@PROCS=" + quote(ed_PROCS.Value); //작업공정
		sArgument += "@DAYGU=" + quote(cmb_DAYGU.Value); //주야구분
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/  
function setFGPZ170(){

	if(ds_FGPZ170.GetRowType(ds_FGPZ170.currow)=="update" ||ds_FGPZ170.GetRowType(ds_FGPZ170.currow)=="insert" ){
		if(!ds_FGPZ170_CanRowPosChange()){
			return;
		}
	}
	if(cmb_DAYGU.Value=="D"){
		sumJobTim();	
	}

	var sKind		= "setFGPZ170";						
	var sMethodName = "service::psfaclib/saveFGPZ170.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_FGPZ170=ds_FGPZ170:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FGPZ170_CHECK=ds_FGPZ170_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_FGPZ170_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_FGPZ170_CHECK.GetRowCount();i++){
			alert("작업일자 : " +ds_FGPZ170_CHECK.GetColumn(i,"JDTFM")+ " = " + ds_FGPZ170_CHECK.GetColumn(i,"RTMSG"));
		}
		getFGPZ170();  //error항목이 존재시에는 재조회
		return;
	}// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	var wrkym = cmb_WRKYMK.Value;
	var procs = ed_PROCS.Text;
	var daygu = cmb_DAYGU.Value;
		cmb_WRKYMK.BKColor="white";
		ed_PROCS.BKColor="white";
		cmb_DAYGU.BKColor="white";

	if (wrkym == ""){
		cmb_WRKYMK.BKColor="paleturquoise";
		alert("[알림]계획년월을 입력하세요.");
		//alert(setPopMessage("6015"));//[알림]계획년월을 입력하세요.
		//setMessage("6015");
		result = false;
	}else if (procs == ""){
		ed_PROCS.BKColor="paleturquoise";
		alert("[알림]작업공정을 입력하세요.");
		//alert(setPopMessage("6016"));//[알림]작업공정을 입력하세요.
		//setMessage("6016");
		result = false;
	}else if (length(daygu) == 0){
		cmb_DAYGU.BKColor="paleturquoise";
		alert("[알림]주야구분을 입력하세요.");
		//alert(setPopMessage("2027"));//[알림] 주야구분을 선택하세요.
		//setMessage("2027");
		result = false;
	}
	return result;
}

