//계획년월 CHECK
function cmb_WRKYMK_OnKillFocus(obj)
{
	if(length(trim(obj.Value)) < 1) return;
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getMST090PF";		
	var sMethodName = "service::pscomlib/selectMST091LF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST091LF=ds_MST091LF";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " PRCYM=" + quote(substr(obj.Value,0,6)); //계획년월
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_MST091LF.GetRowCount() < 1){
		alert(setPopMessage("4003"));
		setMessage("4003");	
		cmb_MKYM.SetFocus();
		return;
	}
	http.usewaitcursor = true;	
}
//작업공정 CHECK
function ed_PROCS_OnKillFocus(obj)
{
	if(length(trim(obj.Value)) < 1) return;
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getFGP100PF";		
	var sMethodName = "service::psfaclib/selectFGP100PF.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "";
	var sOutDataSet = "ds_FGP100PF=ds_FGP100PF";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " PROCS=" + quote(obj.Value); //작업공정
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_FGP100PF.GetRowCount() < 1){
		alert(setPopMessage("6016"));
		setMessage("6016");	
		ed_PROCS.SetFocus();
		return;
	}
	http.usewaitcursor = true;		
}

function ds_FGPZ170_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "JDTFM"){//시작일자Check
		if(varNewValue!=""){
			var setDate = dateChk(varNewValue);
			if(setDate == false){
				return setDate;
			}
			else{
				var getPrcdt =ds_FGPZ170.GetColumn(nRow, "PRCDT");
				if(getPrcdt < varNewValue){ //시작일은 작업일자와 같거나 작아야함.
					alert("[알림]시작일은 작업일자와 같거나 작아야합니다.");
					//alert(setPopMessage("6020"));//[알림]시작일은 작업일자와 같거나 작아야합니다.
					//setMessage("6020");	
					return false;
				}			
			}
		}
	}
	if (strColumnID == "STRTM"){//시작시간Check
		if(varNewValue!=""){
			var setTime = timeChk(varNewValue);
			if(setTime == false){
				return setTime;
			}else if(setTime == true && cmb_DAYGU.Value=="D"){//주간시간 check
				var getDysTm =parseInt(ds_FGP010PF.GetColumn(0, "DYSTM"));			
				var getDyeTm =parseInt(ds_FGP010PF.GetColumn(0, "DYETM"));
				if(parseInt(varNewValue) < getDysTm || parseInt(varNewValue) > getDyeTm) {
					alert("[알림]주간시작시간 범위를 벗어났습니다.");
					//alert(setPopMessage("6021"));//[알림]시작시간 범위를 벗어났습니다.
					//setMessage("6021");	
					return false;
				}		
			}
		}
	}
	if (strColumnID == "JDTTO"){//종료일자Check
		if(varNewValue!=""){
			var setDate = dateChk(varNewValue);
			if(setDate == false){
				return setDate;
			}else{
				var getPrcdt =ds_FGPZ170.GetColumn(nRow, "PRCDT");
				var WRKDT="";
				dateChk2(getPrcdt);
				WRKDT = ds_MST090PF_ROW.GetColumn(0,"PRCDT");
				if(getPrcdt != varNewValue && cmb_DAYGU.Value=="D"){ //주간은 계획일과 시작일이 같아야함.
					alert("[알림]주간작업종료일은 작업일자와 동일해야합니다.");
					//alert(setPopMessage("6022"));//[알림]주간작업종료일은 작업일자와 동일해야합니다.
					//setMessage("6022");					
					return false;
				}else if((getPrcdt != varNewValue  && WRKDT != varNewValue) && cmb_DAYGU.Value=="N"){
					alert("[알림]야간작업종료일은 작업일자와 같거나 하루작아야합니다.");
					//alert(setPopMessage("6023"));//[알림]야간작업종료일은 작업일자와 같거나 하루작아야합니다.
					//setMessage("6023");						
					return false;
				}				
			}
		}
	}

	if (strColumnID == "ENDTM"){//시간Check
		if(varNewValue!=""){
			var setTime = timeChk(varNewValue);
			if(setTime == false){
				return setTime;
			}else if(setTime == true && cmb_DAYGU.Value=="D"){//주간시간 check
				var getDysTm =parseInt(ds_FGP010PF.GetColumn(0, "DYSTM"));			
				var getDyeTm =parseInt(ds_FGP010PF.GetColumn(0, "DYETM"));
				if(parseInt(varNewValue) < getDysTm || parseInt(varNewValue) > getDyeTm) {
					alert("[알림]주간종료시간 범위를 벗어났습니다.");
					//alert(setPopMessage("6024"));//[알림]종료시간 범위를 벗어났습니다.
					//setMessage("6024");	
					return false;
				}		
			}
		}
	}
}

function ds_FGPZ170_CanRowPosChange(obj,nOldRow,nRow)
{
	var result=true;
	if(ds_FGPZ170.GetRowType(ds_FGPZ170.currow)=="update" ||ds_FGPZ170.GetRowType(ds_FGPZ170.currow)=="insert" ){
		var getPrcdt =ds_FGPZ170.GetColumn(ds_FGPZ170.currow, "PRCDT");
		var getJdtfm =ds_FGPZ170.GetColumn(ds_FGPZ170.currow, "JDTFM");
		var getStrtm =ds_FGPZ170.GetColumn(ds_FGPZ170.currow, "STRTM");
		var getJdtto =ds_FGPZ170.GetColumn(ds_FGPZ170.currow, "JDTTO");
		var getEndTm =ds_FGPZ170.GetColumn(ds_FGPZ170.currow, "ENDTM");
		var getContl =ds_FGPZ170.GetColumn(ds_FGPZ170.currow, "CONTL");

		if(length(getJdtfm)!=0){ //시작일자가 있는경우 시작시간, 종료일자 check
			if(length(getStrtm)==0){
				alert("[알림]시작시간을  입력하세요.");
				//alert(setPopMessage("6027"));//[알림]시작시간을  입력하세요.
				//setMessage("6027");	
				return false; 
			}else if(length(getJdtto)==0){
				alert("[알림]종료일자를 입력하세요.");
				//alert(setPopMessage("6026"));//[알림]종료일자를 입력하세요.
				//setMessage("6026");	
				return false; 			
			}//야간시작시간체크
			if(getJdtfm==getPrcdt && cmb_DAYGU.Value=="N"){//야간작업시간 check(야간작업일=지시일)
				if(parseInt(getStrtm) < 0 || parseInt(getStrtm) > parseInt(ds_FGP010PF.GetColumn(0, "NTETM"))){
					alert("[알림]야간1시작시간 범위를 벗어났습니다.");
					//alert(setPopMessage("6021"));//[알림]시작시간 범위를 벗어났습니다.
					//setMessage("6021");	
					return false; 
				}
			}else if(getJdtfm < getPrcdt && cmb_DAYGU.Value=="N"){//야간작업시간 check(야간작업일<지시일)
				if(parseInt(getStrtm) < parseInt(ds_FGP010PF.GetColumn(0, "NTSTM")) || parseInt(getStrtm) >240000){
					alert("[알림]야간2시작시간 범위를 벗어났습니다.");
					//alert(setPopMessage("6021"));//[알림]시작시간 범위를 벗어났습니다.
					//setMessage("6021");	
					return false; 
				}
			}
		}
		if(length(getStrtm)!=0){ //시작시간이 있는경우 시작일자 check
			if(length(getJdtfm)==0){
				alert("[알림]시작일자를 입력하세요.");
				//alert(setPopMessage("6025"));//[알림]시작일자를 입력하세요.
				//setMessage("6025");
				return false; 
			}		
		}
		if(length(getJdtto)!=0){//종료일자가 있는경우 시작일자 check
			if(length(getEndTm)==0){
				alert("[알림]종료시간을  입력하세요.");
				//alert(setPopMessage("6028"));//[알림]종료시간을 입력하세요.
				//setMessage("6028");
				return false; 
			}else if(length(getJdtfm)==0){
				alert("[알림]시작일자를 입력하세요.");
				//alert(setPopMessage("6025"));//[알림]시작일자를 입력하세요.
				//setMessage("6025");
				return false; 			
			}//야간종료시간체크
			if(getJdtto==getPrcdt && cmb_DAYGU.Value=="N"){//야간작업시간 check(야간작업일=지시일)
				if(parseInt(getEndTm) < 0 || parseInt(getEndTm) > parseInt(ds_FGP010PF.GetColumn(0, "NTETM"))){
					alert("[알림]야간1종료시간 범위를 벗어났습니다.");
					//alert(setPopMessage("6024"));//[알림]종료시간 범위를 벗어났습니다.
					//setMessage("6024");
					return false; 
				}
			}else if(getJdtto < getPrcdt && cmb_DAYGU.Value=="N"){//야간작업시간 check(야간작업일<지시일)
				if(parseInt(getEndTm) < parseInt(ds_FGP010PF.GetColumn(0, "NTSTM")) || parseInt(getEndTm) >240000){
					alert("[알림]야간2종료시간 범위를 벗어났습니다.");
					//alert(setPopMessage("6024"));//[알림]종료시간 범위를 벗어났습니다.
					//setMessage("6024");
					return false; 
				}
			}					
		}

		if(length(getStrtm)!=0 && length(getEndTm)!=0 && cmb_DAYGU.Value=="D"){//주간작업시간 check
			if(parseInt(getStrtm) >= parseInt(getEndTm)){
				alert("[확인]시작일자가 종료일자보다 클 수는 없습니다.");
				//alert(setPopMessage("1503"));//[확인] 시작일자가 종료일자보다 클 수는 없습니다.
				//setMessage("1503");	
				return false; 
			}
		}
		//rpg없음. 추가함.
		if(length(getStrtm)!=0 && length(getEndTm)!=0 && cmb_DAYGU.Value=="N"){//야간작업시간 check
			if(getJdtfm == getJdtto && parseInt(getStrtm) >= parseInt(getEndTm)){
				alert("[확인]시작일자가 종료일자보다 클 수는 없습니다.");
				//alert(setPopMessage("1503"));//[확인] 시작일자가 종료일자보다 클 수는 없습니다.
				//setMessage("1503");				
				return false; 
			}
		}

		if(length(getEndTm)!=0){ //종료시간이 있는경우 시작일자 check
			if(length(getJdtto)==0){
				alert("[알림]종료일자를 입력하세요.");
				//alert(setPopMessage("6026"));//[알림]종료일자를 입력하세요.
				//setMessage("6026");
				return false; 
			}		
		}
		if(length(getJdtfm)!=0){
		//주간작업시간계산
		var fhTime="";
		var ehTime="";
		var jobTime=0;
		var noWkTime=0;

		if(cmb_DAYGU.Value=="D"){
			fhTime=parseInt(substr(lpad(getStrtm,0,6),0,2))*60+parseInt(substr(lpad(getStrtm,0,6),2,2));
			ehTime=parseInt(substr(lpad(getEndTm,0,6),0,2))*60+parseInt(substr(lpad(getEndTm,0,6),2,2));
			getFGP120PF(getPrcdt, getStrtm, getEndTm);
			if(ds_FGP120PF.rowcount >0 ){
				noWkTime= parseInt(ds_FGP120PF.GetColumn(0,"STMIN"));
				jobTime= parseInt(ehTime) - parseInt(fhTime);
				ds_FGPZ170.SetColumn(ds_FGPZ170.currow,"JOBTM",jobTime-noWkTime);
			}else{
				getFGP129PF(getStrtm, getEndTm);
				if(ds_FGP129PF.rowcount >0 ){
					noWkTime= parseInt(ds_FGP129PF.GetColumn(0,"STMIN"));
					jobTime= parseInt(ehTime) - parseInt(fhTime);
					ds_FGPZ170.SetColumn(ds_FGPZ170.currow,"JOBTM",jobTime-noWkTime);
				}
			}
		}else if(cmb_DAYGU.Value=="N"){
			var getNtstm=substr(lpad(ds_FGP010PF.GetColumn(0, "NTSTM"),0,6),0,4);
			var getNtetm=substr(lpad(ds_FGP010PF.GetColumn(0, "NTETM"),0,6),0,4);

			//야간시작시각
				//24시이전일때
			if( parseInt(substr(lpad(getStrtm,0,6),0,4)) >= parseInt(getNtstm) && parseInt(substr(lpad(getStrtm,0,6),0,4))<= 2400){
				//alert("24이전시작");
				fhTime= 24*60 - (parseInt(substr(lpad(getStrtm,0,6),0,2))*60+parseInt(substr(lpad(getStrtm,0,6),2,2)));
			}else{//야간시작시각이 00부터 사업부종료시간 이전일때
				//alert("00시이후시작");
				fhTime=parseInt(substr(lpad(getStrtm,0,6),0,2))*60+parseInt(substr(lpad(getStrtm,0,6),2,2));						
			}
			//야간졸료시각
				//24시이전일때
			if( parseInt(substr(lpad(getEndTm,0,6),0,4)) >= parseInt(getNtstm) && parseInt(substr(lpad(getEndTm,0,6),0,4))<= 2400){
				//alert("24이전종료");
				ehTime= 24*60 - (parseInt(substr(lpad(getEndTm,0,6),0,2))*60+parseInt(substr(lpad(getEndTm,0,6),2,2)));						

			}else{//야간종료시각이 00부터 사업부종료시간 이전일때
				//alert("00시이후종료");
				ehTime=parseInt(substr(lpad(getEndTm,0,6),0,2))*60+parseInt(substr(lpad(getEndTm,0,6),2,2));						
			}
//alert(getEndTm+"==="+lpad(getEndTm,0,6)+"=="+parseInt(substr(lpad(getEndTm,0,6),0,4)) +"=="+parseInt(getNtetm));
			if(parseInt(substr(lpad(getEndTm,0,6),0,4)) >= 0 && parseInt(substr(lpad(getEndTm,0,6),0,4))<=parseInt(getNtetm)){
/*
				jobTime= parseInt(ehTime) + parseInt(fhTime);
				ds_FGPZ170.SetColumn(ds_FGPZ170.currow,"JOBTM",jobTime);
				alert("종료시간이00이상 사업부종료시간이하");
*/
				//이부분만 로직이상함.동일날짜계산추가함 rpg없음.
				if(getJdtfm == getJdtto){
					jobTime= parseInt(ehTime) - parseInt(fhTime);
					//ds_FGPZ170.SetColumn(ds_FGPZ170.currow,"JOBTM",jobTime);
					//alert("동일날짜 종료시간이00이상 사업부종료시간이하");
				}
				else{
					jobTime= parseInt(ehTime) + parseInt(fhTime);
					//ds_FGPZ170.SetColumn(ds_FGPZ170.currow,"JOBTM",jobTime);
					//alert("종료시간이00이상 사업부종료시간이하");				
				}
			}
			else{
				if(parseInt(substr(lpad(getEndTm,0,6),0,4)) = 2400){
					jobTime= parseInt(ehTime) + parseInt(fhTime);
					//ds_FGPZ170.SetColumn(ds_FGPZ170.currow,"JOBTM",jobTime);  
				//alert("그외 종료시간이 24시");
				}else{//시간계산이상함.
					jobTime= parseInt(ehTime) - parseInt(fhTime);
					//ds_FGPZ170.SetColumn(ds_FGPZ170.currow,"JOBTM",jobTime);
				//alert("그외 종료시간이 24시가 아닌 경우");
				}
			}
		//시작시간과 종료시간이 사업부시작시간보다 크고 24시보다 작을 경우	
			if((parseInt(substr(lpad(getStrtm,0,6),0,4)) >= parseInt(getNtstm) && parseInt(substr(lpad(getStrtm,0,6),0,4))<= 2400) && 
				(parseInt(substr(lpad(getEndTm,0,6),0,4)) >= parseInt(getNtstm) && parseInt(substr(lpad(getEndTm,0,6),0,4))<= 2400)){
				//alert("시작시간과 종료시간이 사업부시작시간보다 크고 24시보다 작을 경우");
				getFGP120PF(getPrcdt, getStrtm, getEndTm);
				if(ds_FGP120PF.rowcount >0 ){
					noWkTime= parseInt(ds_FGP120PF.GetColumn(0,"STMIN"));
					ds_FGPZ170.SetColumn(ds_FGPZ170.currow,"JOBTM",jobTime-noWkTime);
				}else{
					ds_FGPZ170.SetColumn(ds_FGPZ170.currow,"JOBTM",jobTime);
				}
			}
			else{//시작시간과 종료시간이 0000보다 크고 사업부종료시간보다 작을 경우
				if(parseInt(substr(lpad(getStrtm,0,6),0,4)) >= 0000 && parseInt(substr(lpad(getStrtm,0,6),0,4))<= parseInt(getNtetm) && 
					parseInt(substr(lpad(getEndTm,0,6),0,4)) <= parseInt(getNtetm)){
					//alert("시작시간과 종료시간이 0000보다 크고 사업부종료시간보다 작을 경우");
					getFGP120PF(getPrcdt, getStrtm, getEndTm);
					if(ds_FGP120PF.rowcount >0 ){
						noWkTime= parseInt(ds_FGP120PF.GetColumn(0,"STMIN"));
						ds_FGPZ170.SetColumn(ds_FGPZ170.currow,"JOBTM",jobTime-noWkTime);
					}else{
						ds_FGPZ170.SetColumn(ds_FGPZ170.currow,"JOBTM",jobTime);
					}			
				}
				else{//시작시간이 사업부시작시간보다 크고 24시보다 작을 경우, 종료시간이 0000보다크거나같고 사업부종료시간보다 작거나같음
					if((parseInt(substr(lpad(getStrtm,0,6),0,4)) >= parseInt(getNtstm) && parseInt(substr(lpad(getStrtm,0,6),0,4))<= 2400) && 
						(parseInt(substr(lpad(getEndTm,0,6),0,4)) >=0000 && parseInt(substr(lpad(getEndTm,0,6),0,4)) <= parseInt(getNtetm))){
						//alert("시작시간이 사업부시작시간보다 크고 24시보다 작을 경우, 종료시간이 0000보다크거나같고 사업부종료시간보다 작거나같음");
						getEndTm =parseInt(substr(lpad(getEndTm,0,6),0,4))+2400+"00";
						if(parseInt(substr(lpad(getStrtm,0,6),0,4))>=0000 && parseInt(substr(lpad(getStrtm,0,6),0,4))<= parseInt(getNtetm)){
							getStrtm =parseInt(substr(lpad(getStrtm,0,6),0,4))+2400+"00";
						}
//alert(getEndTm+"===="+getEndTm);
						getFGP120PF(getPrcdt, getStrtm, getEndTm);
						if(ds_FGP120PF.rowcount >0 ){
							noWkTime= parseInt(ds_FGP120PF.GetColumn(0,"STMIN"));
							ds_FGPZ170.SetColumn(ds_FGPZ170.currow,"JOBTM",jobTime-noWkTime);
						}else{
							ds_FGPZ170.SetColumn(ds_FGPZ170.currow,"JOBTM",jobTime);
						}
					}
				}
			}
			if(getContl!="P"){
				ds_FGPZ170.UpdateControl=false;
				ds_FGPZ170.SetRowType(ds_FGPZ170.currow,"insert");
				ds_FGPZ170.UpdateControl=true;
				ds_FGPZ170.SetColumn(ds_FGPZ170.currow,"CONTL","P");
			}
		}
		}
	}
	return result;
}
//시간포맷Check
function timeChk(getTime){
	var rlt = true;
	var msg = "";
	var timeChk = lpad(getTime,0,6);
	if(substr(timeChk,0,2)>24){
		alert("[알림]시간은 24보다 클수없습니다.");
		//alert(setPopMessage("6017"));//[알림]시간은 24보다 클수없습니다.
		//setMessage("6017");
		rlt =false;
	}
	else if(substr(timeChk,2,2)>59){
		alert("[알림]분은 59보다 클수없습니다.");
		//alert(setPopMessage("6018"));//[알림]분은 59보다 클수없습니다.
		//setMessage("6018");
		rlt =false;
	}
	else if(substr(timeChk,4,2)>59){
		alert("[알림]초는 59보다 클수없습니다.");
		//alert(setPopMessage("6019"));//[알림]초는 59보다 클수없습니다.
		//setMessage("6019");
		rlt =false;
	}
	return rlt;
}
//날짜Check
function dateChk(getdate){

	var rlt = true;
	var sKind		= "getMST090PF";		
	var sMethodName = "service::pscomlib/selectMST090PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST090PF=ds_MST090PF";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " PRCDT=" + quote(getdate); //작업일자
	http.Sync 	= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	if (ds_MST090PF.GetRowCount() < 1){
		alert(setPopMessage("4061"));
		setMessage("4061");	
		rlt =false;
	}
	return rlt;
}
