		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " PRCDT=" + quote(prcdt); //작업일
		sArgument += " PROCS=" + quote(ed_PROCS.Text); //공정코드
		sArgument += " DAYGU=" + quote(cmb_DAYGU.Value); //주야구분
		sArgument += " STRTM=" + quote(strtm); //시작시간
		sArgument += " ENDTM=" + quote(endTm); //종료시간
	http.Sync 	= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
}

