function sumJobTim(){
		//주간작업시간계산
	var fhTime="";
	var ehTime="";
	var jobTime=0;
	var noWkTime=0;
	ds_FGPZ170.UpdateControl=false;
	for(i=0; i<ds_FGPZ170.rowcount; i++){
		var getPrcdt =ds_FGPZ170.GetColumn(i, "PRCDT");
		var getJdtfm =ds_FGPZ170.GetColumn(i, "JDTFM");
		var getStrtm =ds_FGPZ170.GetColumn(i, "STRTM");
		var getJdtto =ds_FGPZ170.GetColumn(i, "JDTTO");
		var getEndTm =ds_FGPZ170.GetColumn(i, "ENDTM");
		var getContl =ds_FGPZ170.GetColumn(i, "CONTL");
		var getPrcmkm =ds_FGPZ170.GetColumn(i, "PRCMKM");
		//JOBTM이 없으면 신규INSERT임...				
		if(cmb_DAYGU.Value=="D" && getContl!="P" && length(getJdtfm)!=0){
			fhTime=parseInt(substr(lpad(getStrtm,0,6),0,2))*60+parseInt(substr(lpad(getStrtm,0,6),2,2));
			ehTime=parseInt(substr(lpad(getEndTm,0,6),0,2))*60+parseInt(substr(lpad(getEndTm,0,6),2,2));
			getFGP120PF(getPrcdt, getStrtm, getEndTm);
			if(ds_FGP120PF.rowcount >0 ){
				noWkTime= parseInt(ds_FGP120PF.GetColumn(0,"STMIN"));
				jobTime= parseInt(ehTime) - parseInt(fhTime);
				ds_FGPZ170.SetColumn(i,"JOBTM",jobTime-noWkTime);
			}else{
				getFGP129PF(getStrtm, getEndTm);
				if(ds_FGP129PF.rowcount >0 ){
					noWkTime= parseInt(ds_FGP129PF.GetColumn(0,"STMIN"));
					jobTime= parseInt(ehTime) - parseInt(fhTime);
					ds_FGPZ170.SetColumn(i,"JOBTM",jobTime-noWkTime);
				}
			}
		ds_FGPZ170.SetColumn(i,"CONTL","P");
		ds_FGPZ170.SetRowType(i,"insert");
		}
	}
	ds_FGPZ170.UpdateControl=true;
}
