var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("2.8"); //조회, 추가, 저장
	getComCode(); //공장구분 선택
	//ed_PROCS.Text="C-07";
	//cmb_DAYGU.Value="D";
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false;
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	//MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getFGPZ200(){

	if(!validationChk()){
		return;
	}
	//alert(ed_PASWD.Text);
	if(length(ed_MANCT.Text)!=0 && ed_PASWD.Text=="PSCM"){
		if(confirm("공수재계산을 하시겠습니까?") == false) return ;
		var sKind		= "setFGPZ200_CAL";						
		var sMethodName = "service::psfaclib/saveFGPZ200_CAL.do";    // 서버에서 호출 될 Method 명
		var sInDataSet  = "";               //자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "";  	   // 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += " LIB1=" + quote(Lib1);
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument += " PROCS=" + quote(ed_PROCS.Value); //작업공정
			sArgument += " DAYGU=" + quote(cmb_DAYGU.Value); //주야구분		
			sArgument += " MANCT=" + quote(ed_MANCT.Value); //작업인원		
		http.Sync=true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync=false;
	}
	var sKind		= "getFGPZ200";						
	var sMethodName = "service::psfaclib/selectFGPZ200.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FGPZ200=ds_FGPZ200";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@PROCS=" + quote(ed_PROCS.Value); //작업공정
		sArgument += "@DAYGU=" + quote(cmb_DAYGU.Value); //주야구분
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/  
function setFGPZ200(){


	var sKind		= "setFGPZ200";						
	var sMethodName = "service::psfaclib/saveFGPZ200.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_FGPZ200=ds_FGPZ200:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FGPZ200_CHECK=ds_FGPZ200_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_FGPZ200_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_FGPZ200_CHECK.GetRowCount();i++){
			alert("품번 : " +ds_FGPZ200_CHECK.GetColumn(i,"PRTNO")+ " = " + ds_FGPZ200_CHECK.GetColumn(i,"RTMSG"));
		}
		getFGPZ200();  //error항목이 존재시에는 재조회
		return;
	}// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	var procs = ed_PROCS.Text;
	var daygu = cmb_DAYGU.Value;
		ed_PROCS.BKColor="white";
		cmb_DAYGU.BKColor="white";

	if (procs == ""){
		ed_PROCS.BKColor="paleturquoise";
		alert("[알림]작업공정을 입력하세요.");
		//alert(setPopMessage("6016"));//[알림]작업공정을 입력하세요.
		//setMessage("6016");
		result = false;
	}else if (length(daygu) == 0){
		cmb_DAYGU.BKColor="paleturquoise";
		alert("[알림]주야구분을 입력하세요.");
		//alert(setPopMessage("2027"));//[알림] 주야구분을 선택하세요.
		//setMessage("2027");
		result = false;
	}
	return result;
}

