function setExcel()
{
	var fName   = "전광판택타임마스타";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|전광판택타임마스타";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

