//작업공정 CHECK
function ed_PROCS_OnKillFocus(obj)
{
	if(length(trim(obj.Value)) < 1) return;
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getBM002PR";		
	var sMethodName = "service::psfaclib/selectBM002PR.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "";
	var sOutDataSet = "ds_BM002PR=ds_BM002PR";
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " PROCS=" + quote(obj.Value); //작업공정
		sArgument += " FLAG=" + quote("PROCS");
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_BM002PR.GetRowCount() < 1){
		alert(setPopMessage("6016"));
		setMessage("6016");	
		ed_PROCS.SetFocus();
		return;
	}
	http.usewaitcursor = true;		
}


function ds_FGPZ200_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "CYCTM"){
		if(length(varNewValue)!=0){
			if(length(ds_FGPZ200.GetColumn(nRow,"COMPS"))==0){
				ds_FGPZ200.SetColumn(nRow, "COMPS", ComboBoxPlant.Value); // 사업부 설정
				ds_FGPZ200.SetColumn(nRow, "DAYGU", cmb_DAYGU.Value); // 주야구분 설정
				ds_FGPZ200.UpdateControl=false;
				ds_FGPZ200.SetRowType(nRow,"insert");
				ds_FGPZ200.UpdateControl=true;
			}
		}
	}	
}

function ed_PASWD_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		fn_Search();
	}
}
