function ed_CODE_OnCharChanged(obj,strPreText,strPostText)
{
	st_CODE.Text = "";		
}

function ed_CODE_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getMST650PF";		
	var sMethodName = "service::pscomlib/selectMST650.do";  					
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST650PF=ds_MST650PF";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(COMPS); //사업부
	sArgument += " DPCODE=" + quote(ed_CODE.Text); //부서코드
	//sArgument += " DPEDSW=" + quote("T"); //사용여부 T사용 F미사용
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	if (ds_MST650PF.GetRowCount() < 1){
		alert(setPopMessage("4708"));
		setMessage("4708");	
		ed_CODE.SetFocus();
		return;
	}
	st_CODE.Text=trim(ds_MST650PF.GetColumn(0,"DPCONM"));
	http.usewaitcursor = true;		
}
