//그룹key미사용시 그룹별 카운터
function incSEQ(rowNm){

	var sSeq=1;
	var cSeq=0;
	if( rowNm==0){
		return sSeq;
	}	
	if(ds_FHDC119R.GetColumn(rowNm,"DPTCD") != ds_FHDC119R.GetColumn(rowNm-1,"DPTCD")){
		ds_FHDC119R.SetColumn(rowNm,"ROWNUM",1);
		sSeq=1;
	}
	else{
		cSeq=parseInt(ds_FHDC119R.GetColumn(rowNm-1,"ROWNUM"));
		ds_FHDC119R.SetColumn(rowNm,"ROWNUM",cSeq+1);
		sSeq=cSeq+1;
	}
	return sSeq;	
}
