function getFHDC200(){

	if(!validationChk()){
		return;
	}

	var sKind		= "getFHD000PF";		
	var sMethodName = "service::psfaclib/selectFHD000.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_FHD000PF=ds_FHD000PF";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument  += " CODE=" + quote(ed_CODE.Text);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	if (ds_FHD000PF.rowcount< 1){
		alert(setPopMessage("1002"));
		setMessage("1002");	
		ed_CODE.SetFocus();
		return;
	}

//1.마감처리 -> 등록시 마감여부 확인
	http.Sync=true;
	var sKind		= "getFHD100PF";		
	var sMethodName = "service::psfaclib/selectFHD100_MAGAM.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_FHD100PF_MAGAM=ds_FHD100PF_MAGAM";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " DPCODE=" + quote(ed_CODE.Text);
		sArgument += " WRKDT=" + quote(cmb_WRKDT.Value); //작업일자
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;
	if (ds_FHD100PF_MAGAM.rowcount = 0){
		//alert("[알림] 마감 처리가 진행하지 않은 날짜입니다.");
		alert(setPopMessage("4696"));
		setMessage("4696");	
		return;
	}

	var sKind		= "getFHDC200";						
	var sMethodName = "service::psfaclib/selectFHDC200.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";												// 자료 저장 시 보낼 DataSet 명칭
	var	sOutDataSet = "ds_FHDC200_FHD200=ds_FHDC200_FHD200 ds_FHDC200_FHD300=ds_FHDC200_FHD300 ds_FHDC200_FHD100=ds_FHDC200_FHD100 ds_FHDC200_FHD500=ds_FHDC200_FHD500";	// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " DHCODE=" + quote(ed_CODE.Text); //부서코드
		sArgument += " WRKDT=" + quote(cmb_WRKDT.Value); //작업일자
		sArgument += " EXCD=" + quote("$"); //제외코드
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;
	calTIME();
	calTIME2();
}

function setFHDC200(){

	if(!validationChk()){
		return;
	}
	if(!validationChk2()){
		return;
	}

	var sKind		= "setFHDC200";						
	var sMethodName = "service::psfaclib/saveFHDC200.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_FHDC200_FHD200=ds_FHDC200_FHD200:U ds_FHDC200_FHD300=ds_FHDC200_FHD300:U ds_FHDC200_FHD500=ds_FHDC200_FHD500:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FHDC200_CHECK=ds_FHDC200_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		//sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " DHCODE=" + quote(ed_CODE.Text); //부서코드
		sArgument += " WRKDT=" + quote(cmb_WRKDT.Value); //작업일자
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;
}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){

	if (ds_FHDC200_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_FHDC200_CHECK.GetRowCount();i++){
			alert("코드 : " +ds_FHDC200_CHECK.GetColumn(i,"MGMCD") +" 코드명 : " +ds_FHDC200_CHECK.GetColumn(i,"MGMNM") + " = " + ds_FMSZ110_CHECK.GetColumn(i,"RTMSG"));
		}
		getFHDC200();  //error항목이 존재시에는 재조회
		return;
	}// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else{
		switch (svcId){
			case "getFHDC200" :
				if(ds_FHDC200_FHD200.rowcount <= 0){  //data값이 없을때
					//alert("조회된거 없다!");
					alert(setPopMessage("1003"));
					setMessage("1003");	
				}else{ //정상조회
					//alert("조회됐다.!");
					alert(setPopMessage("1004"));
					setMessage("1004");
				}
				break;
			case "setFHDC200" :
				alert(setPopMessage("1001"));
				setMessage("1001");	
				break;
		}
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	ed_CODE.BKColor="white";
	cmb_WRKDT.BKColor="white";

	if(length(trim(st_CODE.Text)) == 0){
		ed_CODE.BKColor="paleturquoise";
		alert(setPopMessage("6080"));//[알림]작업반을 입력하세요.
		setMessage("6080");
		result = false;
	}else if(cmb_WRKDT.Value == ""){
		cmb_WRKDT.BKColor="paleturquoise";
		alert(setPopMessage("6075"));//[알림]작업일자를 입력하세요.
		setMessage("6075");
		result = false;
	}
	return result;
}

function validationChk2(){

	var result = true;
	displayDiv.st_NONTMKP.DisableBkColor="white";
	displayDiv.st_NONTMC.DisableBkColor="white";
	displayDiv.st_NIGTMKP.DisableBkColor="white";
	displayDiv.st_NIGTMC.DisableBkColor="white";

	var dayTotTime1=0;
	var nightTotTime1=0;
	var dayTotTime2=0;
	var nightTotTime2=0;

		dayTotTime1=displayDiv.st_NONTMKP.Value;
		nightTotTime1=displayDiv.st_NIGTMKP.Value;
		dayTotTime2=displayDiv.st_NONTMC.Value;
		nightTotTime2=displayDiv.st_NIGTMC.Value;

	if(dayTotTime1!=dayTotTime2){
		displayDiv.st_NONTMKP.DisableBkColor="paleturquoise";
		displayDiv.st_NONTMC.DisableBkColor="paleturquoise";
		//alert("[주간]작업일보 총 작업시간과 제품별 총 작업시간이 다릅니다.");
		alert(setPopMessage("6115"));//[알림] 주간 작업일보 총 작업시간과 제품별 총 작업시간이 다릅니다.
		setMessage("6115");
		result = false;
	}
	else if(nightTotTime1!=nightTotTime2){
		displayDiv.st_NIGTMKP.DisableBkColor="paleturquoise";
		displayDiv.st_NIGTMC.DisableBkColor="paleturquoise";
		//alert("[야간]작업일보 총 작업시간과 제품별 총 작업시간이 다릅니다.");
		alert(setPopMessage("6116"));//[알림] 야간 작업일보 총 작업시간과 제품별 총 작업시간이 다릅니다.
		setMessage("6116");
		result = false;
	}
	return result;
}
