function calTIME(){
	//주간시간＝총원-결원-지각-조퇴-외출-지원보냄(파견)+지원받음-(주간제외시간-주간관리시간)
	var nontmkp=0;
	var tottm=0;
	var outtm=0;
	var ltwttm=0;
	var lewttm=0;
	var otwttm=0;
	var sndjtm=0;
	var rcvjtm=0;
	var nontmx=0;
	//잔업시간=잔업시간-지원보냄(파견)+지원받음(야간)-(주간제외시간+주간관리시간)
	var nigtmkp=0;
	var janttm=0;
	var sndytm=0;
	var rcvytm=0;
	var nigtmx=0;

	//alert(ds_FHDC200_FHD100.rowcount);
	if(ds_FHDC200_FHD100.rowcount>0){
		tottm=ds_FHDC200_FHD100.GetColumn(0,"TOTTM");
		outtm=ds_FHDC200_FHD100.GetColumn(0,"OUTTM");
		ltwttm=ds_FHDC200_FHD100.GetColumn(0,"LTWTTM");
		lewttm=ds_FHDC200_FHD100.GetColumn(0,"LEWTTM");
		otwttm=ds_FHDC200_FHD100.GetColumn(0,"OTWTTM");
		sndjtm=ds_FHDC200_FHD100.GetColumn(0,"SNDJTM");
		rcvjtm=ds_FHDC200_FHD100.GetColumn(0,"RCVJTM");	

		janttm=ds_FHDC200_FHD100.GetColumn(0,"JANTTM");	
		sndytm=ds_FHDC200_FHD100.GetColumn(0,"SNDYTM");	
		rcvytm=ds_FHDC200_FHD100.GetColumn(0,"RCVYTM");			
	}

	if(ds_FHDC200_FHD200.rowcount>0){
		for(i=0; i<ds_FHDC200_FHD200.rowcount; i++){
			nontmx+=ds_FHDC200_FHD200.GetColumn(i,"EXJTM");
			nigtmx+=ds_FHDC200_FHD200.GetColumn(i,"EXYTM");
		}
	}

	if(ds_FHDC200_FHD300.rowcount>0){
		for(i=0; i<ds_FHDC200_FHD300.rowcount; i++){
			nontmx+=ds_FHDC200_FHD300.GetColumn(i,"ADJTM");
			nigtmx+=ds_FHDC200_FHD300.GetColumn(i,"ADYTM");
		}
	}
		nontmkp = tottm - outtm - ltwttm - lewttm - otwttm - sndjtm + rcvjtm - nontmx;
		nigtmkp = janttm - sndytm + rcvytm - nigtmx;
		displayDiv.st_NONTMKP.Value=nontmkp;
		displayDiv.st_NIGTMKP.Value=nigtmkp;
}

function calTIME2(){
	//주간시간＝총원-결원-지각-조퇴-외출-지원보냄(파견)+지원받음-(주간제외시간-주간관리시간)
	var nontmc=0;
	var nigtmc=0;

	if(ds_FHDC200_FHD500.rowcount>0){
		for(i=0; i<ds_FHDC200_FHD500.rowcount; i++){
			nontmc+=ds_FHDC200_FHD500.GetColumn(i,"NONTM");
			nigtmc+=ds_FHDC200_FHD500.GetColumn(i,"NIGTM");
		}
	}
	displayDiv.st_NONTMC.Value=nontmc;
	displayDiv.st_NIGTMC.Value=nigtmc;
}
