function getFHDC206Q(){

	if(!validationChk()){
		return;
	}
	var st_DPPAT1, st_DHCODE;
	if(ComboBoxPlant.Value=="S1"){ //KAS만 해당
		st_DPPAT1= "('3326', '3079', '3021', '3321', '3014', '3243', '3194', '3195', '3178', '3198', '3200', '3179', '9999')";
		st_DHCODE= "('3082', '3091', '3166', '3313', '3323', '3128', '3344', '3396', '3308', '3043', '3012', '3164', '3155', '3336', '3339', '3346', '3404', '3256', '9999')";
	}else{	//KAC/KAH 해당
		st_DPPAT1= "('3326', '3079', '3021', '3321', '3014', '3243', '3194', '3195', '3178', '3198', '3200', '3179')";
		//st_DHCODE= "('3082', '3091', '3166', '3313', '3323', '3128', '3344', '3396', '3308', '3043', '3012', '3164', '3155', '3336', '3339', '3346', '3404', '3256') ";
		//2015.05.08 (3023,3106추가)
		st_DHCODE= "('3082', '3091', '3166', '3313', '3323', '3128', '3344', '3396', '3308', '3043', '3012', '3164', '3155', '3336', '3339', '3346', '3404', '3256', '3023', '3106') ";
	}
	//직접시간(FHD501LF) 및 손실/제외/간접시간(FHD201LF, FHD301LF) 코드 생성
	//직접시간코드 ds_FHDC206Q_CODE1(MST050PF)
	//손실/제외/간접시간코드 ds_FHDC206Q_CODE2(FHD060PF)
	var timeCode="";	//코드쿼리생성
	var timeTot="";	//시간합계
	var timeLast="";	//최종합계
	http.Sync=true;
	var sKind		= "getFHDC206Q_TIMECODE";		
	var sMethodName = "service::psfaclib/selectFHDC206Q_TIMECODE.do"; 
	var sInDataSet  = "";
	//var sOutDataSet = "ds_FHDC206Q_TIMECODE=ds_FHDC206Q_TIMECODE";
	var sOutDataSet = "ds_FHDC206Q_CODE1=ds_FHDC206Q_CODE1 ds_FHDC206Q_CODE2=ds_FHDC206Q_CODE2 ";
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " ETC=" + quote("$"); //제외코드
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	
	//중간합계 강제생성
	var selCode="";
	var chkCode1="30";
	var chkCode2="40";		
	for(i=0; i<ds_FHDC206Q_CODE2.rowcount; i++){
		selCode=ds_FHDC206Q_CODE2.GetColumn(i,"CODE");
		if(selcode>chkCode1){
			ds_FHDC206Q_CODE2.InsertRow(i);
			ds_FHDC206Q_CODE2.SetColumn(i,"COMPS",ComboBoxPlant.Value);
			ds_FHDC206Q_CODE2.SetColumn(i,"CODE","H1");
			ds_FHDC206Q_CODE2.SetColumn(i,"CODENM","*손실합계*");
			ds_FHDC206Q_CODE2.SetColumn(i,"DAY","DAYH1");
			ds_FHDC206Q_CODE2.SetColumn(i,"NIGHT","NIGHTH1");
			chkCode1="9999999";
		}
		if(selcode>chkCode2){
			ds_FHDC206Q_CODE2.InsertRow(i);
			ds_FHDC206Q_CODE2.SetColumn(i,"COMPS",ComboBoxPlant.Value);
			ds_FHDC206Q_CODE2.SetColumn(i,"CODE","H2");
			ds_FHDC206Q_CODE2.SetColumn(i,"CODENM","*제외합계*");
			ds_FHDC206Q_CODE2.SetColumn(i,"DAY","DAYH2");
			ds_FHDC206Q_CODE2.SetColumn(i,"NIGHT","NIGHTH2");
			chkCode2="9999999";
		}
	}	
	ds_FHDC206Q_CODE2.AddRow();
	ds_FHDC206Q_CODE2.SetColumn(ds_FHDC206Q_CODE2.row,"COMPS",ComboBoxPlant.Value);
	ds_FHDC206Q_CODE2.SetColumn(ds_FHDC206Q_CODE2.row,"CODE","H3");
	ds_FHDC206Q_CODE2.SetColumn(ds_FHDC206Q_CODE2.row,"CODENM","*간접합계*");
	ds_FHDC206Q_CODE2.SetColumn(ds_FHDC206Q_CODE2.row,"DAY","DAYH3");
	ds_FHDC206Q_CODE2.SetColumn(ds_FHDC206Q_CODE2.row,"NIGHT","NIGHTH3");
	ds_FHDC206Q_CODE2.AddRow();
	ds_FHDC206Q_CODE2.SetColumn(ds_FHDC206Q_CODE2.row,"COMPS",ComboBoxPlant.Value);
	ds_FHDC206Q_CODE2.SetColumn(ds_FHDC206Q_CODE2.row,"CODE","HT");
	ds_FHDC206Q_CODE2.SetColumn(ds_FHDC206Q_CODE2.row,"CODENM","*간접총계*");
	ds_FHDC206Q_CODE2.SetColumn(ds_FHDC206Q_CODE2.row,"DAY","DAYHT");
	ds_FHDC206Q_CODE2.SetColumn(ds_FHDC206Q_CODE2.row,"NIGHT","NIGHTHT");


	//코드 일괄처리
	ds_FHDC206Q_TIMECODE.Copy(ds_FHDC206Q_CODE1);
	for(i=0; i<ds_FHDC206Q_CODE2.rowcount; i++){
		ds_FHDC206Q_TIMECODE.AddRow();
		ds_FHDC206Q_TIMECODE.SetColumn(ds_FHDC206Q_TIMECODE.row,"COMPS",ds_FHDC206Q_CODE2.GetColumn(i,"COMPS"));
		ds_FHDC206Q_TIMECODE.SetColumn(ds_FHDC206Q_TIMECODE.row,"CODE",ds_FHDC206Q_CODE2.GetColumn(i,"CODE"));
		ds_FHDC206Q_TIMECODE.SetColumn(ds_FHDC206Q_TIMECODE.row,"CODENM",ds_FHDC206Q_CODE2.GetColumn(i,"CODENM"));
		ds_FHDC206Q_TIMECODE.SetColumn(ds_FHDC206Q_TIMECODE.row,"DAY",ds_FHDC206Q_CODE2.GetColumn(i,"DAY"));
		ds_FHDC206Q_TIMECODE.SetColumn(ds_FHDC206Q_TIMECODE.row,"NIGHT",ds_FHDC206Q_CODE2.GetColumn(i,"NIGHT"));
	}
	//trace(ds_FHDC206Q_TIMECODE.SaveXML());

	http.Sync=false;
	var Grd_Cellcnt = 0;
		Grd_Cellcnt= ds_FHDC206Q_TIMECODE.rowcount;
	if (Grd_Cellcnt< 0){
		alert("코드가 없습니다.");
		//alert(setPopMessage("4697"));
		//setMessage("4697");	
		return;
	}else{
		for(i=0; i<Grd_Cellcnt; i++){
			var getCode=ds_FHDC206Q_TIMECODE.GetColumn(i,"CODE");
			if(getCode!="H1" && getCode!="H2" && getCode!="H3" && getCode!="HT"){
				timeCode+=", CASE WHEN A.CODEV=SUBSTR('"+ds_FHDC206Q_TIMECODE.GetColumn(i,"DAY")+"', 4) THEN A.TMDAY ELSE 0 END AS "+ds_FHDC206Q_TIMECODE.GetColumn(i,"DAY");				timeCode+=", CASE WHEN A.CODEV=SUBSTR('"+ds_FHDC206Q_TIMECODE.GetColumn(i,"NIGHT")+"', 6) THEN A.TMNIGHT ELSE 0 END AS "+ds_FHDC206Q_TIMECODE.GetColumn(i,"NIGHT");		
			}
			timeTot+=", ROUND(Decimal(SUM(A."+ds_FHDC206Q_TIMECODE.GetColumn(i,"DAY")+"))/60,1) AS " +ds_FHDC206Q_TIMECODE.GetColumn(i,"DAY"); 
			timeTot+=", ROUND(Decimal(SUM(A."+ds_FHDC206Q_TIMECODE.GetColumn(i,"NIGHT")+"))/60,1) AS " +ds_FHDC206Q_TIMECODE.GetColumn(i,"NIGHT"); 

			timeLast+=", F."+ds_FHDC206Q_TIMECODE.GetColumn(i,"DAY")+" AS " +ds_FHDC206Q_TIMECODE.GetColumn(i,"DAY"); 
			timeLast+=", F."+ds_FHDC206Q_TIMECODE.GetColumn(i,"NIGHT")+" AS " +ds_FHDC206Q_TIMECODE.GetColumn(i,"NIGHT"); 
		}

		timeCode+=", CASE WHEN A.CODEV >= '11' AND A.CODEV <= '30' THEN A.TMDAY ELSE 0 END AS DAYH1";
		timeCode+=", CASE WHEN A.CODEV >= '11' AND A.CODEV <= '30' THEN A.TMNIGHT ELSE 0 END AS NIGHTH1";
		timeCode+=", CASE WHEN A.CODEV >= '31' AND A.CODEV <= '40' THEN A.TMDAY ELSE 0 END AS DAYH2";
		timeCode+=", CASE WHEN A.CODEV >= '31' AND A.CODEV <= '40' THEN A.TMNIGHT ELSE 0 END AS NIGHTH2";
		timeCode+=", CASE WHEN A.CODEV >= '41' AND A.CODEV <= '60' THEN A.TMDAY ELSE 0 END AS DAYH3";
		timeCode+=", CASE WHEN A.CODEV >= '41' AND A.CODEV <= '60' THEN A.TMNIGHT ELSE 0 END AS NIGHTH3";
		timeCode+=", CASE WHEN A.CODEV >= '11' AND A.CODEV <= '60' THEN A.TMDAY ELSE 0 END AS DAYHT";
		timeCode+=", CASE WHEN A.CODEV >= '11' AND A.CODEV <= '60' THEN A.TMNIGHT ELSE 0 END AS NIGHTHT";
		
	var Grd_Cellcnt1 = 0;
	var Grd_Cellcnt2 = 0;
		Grd_Cellcnt1= ds_FHDC206Q_CODE1.rowcount;
		Grd_Cellcnt2= ds_FHDC206Q_CODE2.rowcount;
		
		MainGrid2.contents = fn_makeGrdContents2(MainGrid2, Grd_Cellcnt1);
		MainGrid3.contents = fn_makeGrdContents3(MainGrid3, Grd_Cellcnt2);
	}
	var sKind		= "getFHDC206Q";						
	var sMethodName = "service::psfaclib/selectFHDC206Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var	sOutDataSet = "ds_FHDC206Q=ds_FHDC206Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@WRKDTF=" + quote(cmb_WRKDTF.Value); //근태일자FR
		sArgument += "@WRKDTT=" + quote(cmb_WRKDTT.Value); //근태일자TO
		sArgument += "@DPPAT1=" + quote(st_DPPAT1); //팀코드
		sArgument += "@DHCODE=" + quote(st_DHCODE); //부서코드
		sArgument += "@ETC=" + quote("$"); //제외코드
		sArgument += "@TIMECODE=" + quote(timeCode); //SQL1
		sArgument += "@TIMETOT=" + quote(timeTot); //SQL2
		sArgument += "@TIMELAST=" + quote(timeLast); //SQL3
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
 
function validationChk(){
	var result = true;
	cmb_WRKDTF.BKColor="white";
	cmb_WRKDTT.BKColor="white";
		
	if (cmb_WRKDTF.Value == ""){
		cmb_WRKDTF.BKColor="paleturquoise";
		alert(setPopMessage("6075"));//[알림]작업일자를 입력하세요.
		setMessage("6075");
		result = false;
	}else if(cmb_WRKDTT.Value == ""){
		cmb_WRKDTT.BKColor="paleturquoise";
		alert(setPopMessage("6075"));//[알림]작업일자를 입력하세요.
		setMessage("6075");
		result = false;
	}else if(cmb_WRKDTF.Value > cmb_WRKDTT.Value){
		cmb_WRKDTF.BKColor="paleturquoise";
		cmb_WRKDTT.BKColor="paleturquoise";
		alert(setPopMessage("4061"));//[알림]작업일자를 확인하세요.
		setMessage("4061");
		result = false;
	}
	return result;
}

