/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "작업시간집계표";
	var arrSheets  =  Array();  
	
	arrSheets[0]  = "MainGrid3|작업시간집계표_간접";
	arrSheets[1]  = "MainGrid2|작업시간집계표_직접";
	arrSheets[2]  = "MainGrid|작업시간집계표_총장";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);	
}

