//라인별 제외, 직/간접 일괄로 보여줌.
function fn_makeGrdContents2(obj, Cellcnt)
{
	var strContents = "";
	strContents += "<contents>                                                ";
	strContents += "	<format id='Default'>                                 "; 
	strContents += "		<columns>                                         ";
	strContents += "			<col fix='left' width='40'/>                  ";
	strContents += "			<col fix='left' width='120'/>                 ";
	strContents += "			<col fix='left' width='40'/>                  ";
	

	for(var i=0; i<Cellcnt; i++) {
		strContents += "		<col width='65'/>							  ";
		strContents += "		<col width='65'/>							  ";
	}
	strContents += "		</columns>                                        "; 
	strContents += "		<head>                                            ";
	strContents += "			<cell bkcolor='#dfdfdf' col='0' color='#002043' display='text' rowspan='2' text='사업부' />";
	strContents += "			<cell bkcolor='#dfdfdf' col='1' color='#002043'  colspan='2'  display='text' rowspan='2' text='작업장' />";

	for(var j=0; j<Cellcnt; j++) {
		var fieldnm = ds_FHDC206Q_CODE1.GetColumn(j,"CODENM");
			strContents += "			<cell bkcolor='#dfdfdf' col='"+ (j+3+j) +"' color='#002043' colspan='2' display='text' text='"+ fieldnm +"' />";
			strContents += "			<cell bkcolor='#dfdfdf' col='"+ (j+3+j) +"' color='#002043' display='text' row='1' text='주간'/> ";
			strContents += "			<cell bkcolor='#dfdfdf' col='"+ (j+4+j) +"' color='#002043' display='text' row='1' text='야간'/> ";
	}

	strContents += "		</head>                                           ";
	
	strContents += "		<body>                                            "; 	
	strContents += "			<cell align='center' bkcolor='expr:decode(rowlevel,0,'white',1,'white',2,'white',3,'white')' checklength='Byte' col='0' colid='DHCOMP' display='normal' limit='1' suppress='1' TrimType='Both' useime='false'/>";
	strContents += "			<cell align='left' bkcolor='expr:decode(rowlevel,0,&quot;white&quot;,1,&quot;lightcyan&quot;,2,&quot;lightgoldenrodyellow&quot;,3,&quot;lightcoral&quot;)' checklength='Byte' col='1' colid='PDEPTM' display='normal' limit='1' subsumexpr='decode(rowlevel,0,PDEPTM,1,&quot;소계&quot;,2,&quot;총계&quot;,3,&quot;&quot;,4,&quot;&quot;)' suppress='2' TrimType='Both' useime='false'/>";
	strContents += "			<cell align='left' bkcolor='expr:decode(rowlevel,0,&quot;white&quot;,1,&quot;lightcyan&quot;,2,&quot;lightgoldenrodyellow&quot;,3,&quot;lightcoral&quot;)' checklength='Byte' col='2' colid='DHCODE' display='normal' limit='1' subsumexpr='decode(rowlevel,0,DHCODE,1,&quot;&quot;,2,&quot;&quot;,3,&quot;&quot;,4,&quot;&quot;)' suppress='2' TrimType='Both' useime='false'/>";


	for(var k=0; k<=Cellcnt; k++) {	
		var field1 = ds_FHDC206Q_CODE1.GetColumn(k,"DAY");
		var field2 = ds_FHDC206Q_CODE1.GetColumn(k,"NIGHT");

	strContents += "			<cell align='right' bkcolor='expr:decode(rowlevel,0,&quot;white&quot;,1,&quot;lightcyan&quot;,2,&quot;lightgoldenrodyellow&quot;,3,&quot;lightcoral&quot;)' checklength='Byte' col='"+ (k+3+k) +"' colid='"+field1+"' display='number' limit='1' Mask='#,##0.0' subsumexpr='"+field1+".SUM' TrimType='Both' useime='false'/>";
	strContents += "			<cell align='right' bkcolor='expr:decode(rowlevel,0,&quot;white&quot;,1,&quot;lightcyan&quot;,2,&quot;lightgoldenrodyellow&quot;,3,&quot;lightcoral&quot;)' checklength='Byte' col='"+ (k+4+k) +"' colid='"+field2+"' display='number' limit='1' Mask='#,##0.0' subsumexpr='"+field2+".SUM' TrimType='Both' useime='false'/>";
		}

	strContents += "		</body>                                           ";
	strContents += "	</format>                                             "; 
	strContents += "</contents>	                                              "; 
	
	return strContents;
}
