		}

	strContents += "		</body>                                           ";
	strContents += "	</format>                                             "; 
	strContents += "</contents>	                                              "; 
	
	return strContents;
}
function rdoggub_OnClick(obj)
{
	if(rdoggub.Value=1){
		MainGrid.Visible=true;
		MainGrid2.Visible=false;
		MainGrid3.Visible=false;
	}else if(rdoggub.Value=2){
		MainGrid.Visible=false;
		MainGrid2.Visible=true;
		MainGrid3.Visible=false;
	}else if(rdoggub.Value=3){
		MainGrid.Visible=false;
		MainGrid2.Visible=false;
		MainGrid3.Visible=true;
	}
}
