/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "개인별 작업시간 집계표";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|개인별 작업시간 집계표";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

