function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("2.2"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	1.3
	getComCode(); //공장구분 선택
	MainGrid.UserData = MainGrid.Height;
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 
}
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getFHDC610(){

	if(!validationChk()){
		return;
	}
//1.마감처리 -> 등록시 마감여부 확인
	http.Sync=true;
	var sKind		= "getFHD600PF";		
	var sMethodName = "service::psfaclib/selectFHD600_MAGAM.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_FHD600PF_MAGAM=ds_FHD600PF_MAGAM";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " DPCODE=" + quote(ed_CODE.Text);
		sArgument += " WRKDT=" + quote(cmb_WRKDT.Value); //작업일자
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	if (ds_FHD600PF_MAGAM.GetRowCount() > 0){
		//alert("[알림] 마감 처리가 진행된 날짜입니다.");
		alert(setPopMessage("4697"));
		setMessage("4697");	
		return;
	}

	cmb_WRKDT_OnKillFocus();	
	var sKind		= "getFHDC610";						
	var sMethodName = "service::psfaclib/selectFHDC610.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FHDC610=ds_FHDC610";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@LIB3=" + quote(Lib3);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@CODE=" + quote(ed_CODE.Text); //사업부
		sArgument += "@WRKDT=" + quote(cmb_WRKDT.Value); //사업부
		sArgument += "@WEKSDT=" + quote(ds_WRKTM.GetColumn(0,"WEKSDT")); //월요일
		sArgument += "@WEKEDT=" + quote(ds_WRKTM.GetColumn(0,"WEKEDT")); //금요일

	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //erro r발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 **********************************************************************/ 
 function setFHDC610(){

	if(ds_FHDC610.GetRowType(ds_FHDC610.currow)=="update" ||ds_FHDC610.GetRowType(ds_FHDC610.currow)=="insert" ){
		if(!ds_FHDC610_CanRowPosChange()){
			return;
		}
	} 
	if(length(trim(st_CODE.Text)) < 1){
		alert(setPopMessage("2544")); //[확인] 작업일자를 확인바랍니다.
		setMessage("2544");	
		ed_CODE.SetFocus();
		return;
	}	
	if(cmb_WRKDT.Value == ""){
		alert(setPopMessage("4061"));  //[경고] 필수입력항목입니다. (부서코드)
		setMessage("4061");	
		cmb_WRKDT.SetFocus();
		return;
	}

//1.마감처리 -> 등록시 마감여부 확인
	http.Sync=true;
	var sKind		= "getFHD600PF";		
	var sMethodName = "service::psfaclib/selectFHD600_MAGAM.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_FHD600PF_MAGAM=ds_FHD600PF_MAGAM";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " DPCODE=" + quote(ed_CODE.Text);
		sArgument += " WRKDT=" + quote(cmb_WRKDT.Value); //작업일자
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	if (ds_FHD600PF_MAGAM.GetRowCount() > 0){
		//alert("[알림] 마감 처리가 진행된 날짜입니다.");
		alert(setPopMessage("4697"));
		setMessage("4697");	
		return;
	}

	var sKind		= "setFHDC610";						
	var sMethodName = "service::psfaclib/saveFHDC610.do";
	var sInDataSet  = "ds_FHDC610=ds_FHDC610:U";
	var sOutDataSet = "ds_FHDC610_CHECK=ds_FHDC610_CHECK";
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		//sArgument += " LIB3=" + quote(Lib3);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;

	var sKind		= "setmst040";						
	var sMethodName = "service::pscomlib/saveMST040.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    		 								// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += " MAJCD=" + quote("FHD610PF"+ed_CODE.Text);
	sArgument += " MAJDT=" + quote(cmb_WRKDT.Value);
	sArgument += " MAJMK=" + quote("일일근태확정작업");
	sArgument += " MJINT=" + quote(0);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
//trace(ds_FHDC610.SaveXML());

 }

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	ed_CODE.BKColor="white";	
	cmb_WRKDT.BKColor="white";	

	if(length(trim(st_CODE.Text)) < 1)
	{
		ed_CODE.BKColor="paleturquoise";
		//alert(setPopMessage("2544"));
		alert("[경고] 필수입력항목입니다. (부서코드)");
		result =false;
	}
	else if(cmb_WRKDT.Value=="")
	{
		cmb_WRKDT.BKColor="paleturquoise";
		//alert(setPopMessage("4061"));
		alert("[확인] 작업일자를 확인바랍니다.");
		result =false;
	}
	return result;
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	//trace(ds_SLTZ101_CHECK.SaveXML());
	if (ds_FHDC610_CHECK.GetRowCount() > 0){			
		for(var i=0;i<ds_FHDC610_CHECK.GetRowCount();i++){		
			alert("사번 : " +ds_FHDC610_CHECK.GetColumn(i,"PESABN") + ",작업 : " +ds_FHDC610_CHECK.GetColumn(i,"WRKCD") + " = " + ds_FHDC610_CHECK.GetColumn(i,"RTMSG"));
		}
		getFHDC610();        //error항목이 존재시에는 재조회		
		return;		
	}	
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		//setSLTZ101_110();
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

