function fn_Input()
{/*
	if(checkInsertCount("ds_FHDC610")== false) return;
	if(ds_FHDC610.rowcount < 1) fn_Search();  
	ds_FHDC610.AddRow();
	ds_FHDC610.SetColumn(ds_FHDC610.currow,"DHCOMP",ComboBoxPlant.Value);
	ds_FHDC610.SetColumn(ds_FHDC610.currow,"DHCODE",ed_CODE.Text);
	ds_FHDC610.SetColumn(ds_FHDC610.currow,"WRKDT",cmb_WRKDT.Value);
*/
	if(checkInsertCount("ds_FHDC610")== false) return;

	if(!validationChk()){
		return;
	}

	if(ds_FHDC610.rowcount < 1) fn_Search(); 

	if (ds_FHD600PF_MAGAM.rowcount > 0){
		return;
	}

	if(ds_FHDC610.rowcount < 1 ){
		var sKind		= "getFHDC610_ADD";		
		var sMethodName = "service::psfaclib/selectFHDC610_ADD.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_FHDC610=ds_FHDC610";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += " LIB1=" + quote(Lib1);
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " LIB3=" + quote(Lib3);
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument += " CODE=" + quote(ed_CODE.Text); //사업부
			sArgument += " WRKDT=" + quote(cmb_WRKDT.Value); //사업부
			sArgument += " WEKSDT=" + quote(ds_WRKTM.GetColumn(0,"WEKSDT")); //월요일
			sArgument += " WEKEDT=" + quote(ds_WRKTM.GetColumn(0,"WEKEDT")); //금요일
		http.Sync=true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync=false;
		for(i=0; i<ds_FHDC610.rowcount; i++){
			ds_FHDC610.UpdateControl=false;
			ds_FHDC610.SetRowType(i,"insert");
			ds_FHDC610.UpdateControl=true;
		}
	}	
	ds_FHDC610.AddRow();
	ds_FHDC610.SetColumn(ds_FHDC610.currow,"DHCOMP",ComboBoxPlant.Value);
	ds_FHDC610.SetColumn(ds_FHDC610.currow,"DHCODE",ed_CODE.Text);
	ds_FHDC610.SetColumn(ds_FHDC610.currow,"WRKDT",cmb_WRKDT.Value);
	ds_FHDC610.SetColumn(ds_FHDC610.currow,"SPJTM",0);//주지원시간
	ds_FHDC610.SetColumn(ds_FHDC610.currow,"SPYTM",0);//야지원시간
	ds_FHDC610.SetColumn(ds_FHDC610.currow,"LEWTM",0);//조퇴
	ds_FHDC610.SetColumn(ds_FHDC610.currow,"LTWTM",0);//지각
	ds_FHDC610.SetColumn(ds_FHDC610.currow,"OTWTM",0);//외출
	ds_FHDC610.SetColumn(ds_FHDC610.currow,"WEEKMN",0);//외출
	ds_FHDC610.SetColumn(ds_FHDC610.currow,"TMPWEEKMN",0);//외출
}

