function ds_FHDC610_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "PESABN"){//작업자 확인

		if(varNewValue!=""){
			//중복Check ==> 초출 및 정상근무. 잔업 여러개 등록가능함
/*			
			if(ds_FHDC610.FindRow("PESABN", trim(varNewValue)) >= 0){
				alert("[경고] 이미 등록된 자료입니다.");  //[경고] 이미 등록된 자료입니다.
				//alert(setPopMessage("1520"));  //[경고] 이미 등록된 자료입니다.
				//setMessage("1520");	
				return false;
			}
*/			
			//인사마스터
			http.Sync = true;
			var sKind		= "getHMD020PF";		
			var sMethodName = "service::pscomlib/selectINSAMasterPopup.do"; 
			var sInDataSet  = "";
			var sOutDataSet = "ds_HMD020PF=ds_Select";
			var sArgument   = "";
				sArgument  += " LIB2=" + quote(Lib2);
				sArgument  += " LIB3=" + quote(Lib3);
				sArgument  += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
				sArgument  += " DPTCD=" + quote(ed_CODE.Text);
				sArgument  += " SABUN=" + quote(varNewValue);
				sArgument  += " PAYCD=" + quote(2);//1이면 기능직 2이면 직반장
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync = false;
			if (ds_HMD020PF.GetRowCount() < 1){
				alert(setPopMessage("4062")); //[확인] 사용자가 존재하지 않습니다.
				setMessage("4062");	
				return false;
			}
			//else{ //휴직/파견계 조회
			http.Sync = true;
