			WRKTIME= ds_WEEKTM.GetColumn(0,"WEEKMN");
			//9260808
		}
	}
	if (strColumnID == "WRKCD"){//작업코드	

		if(varNewValue!=""){
			if(varNewValue=="29"){//작업코드 29는 토요일만사용가능 //rgp에는T 토요일은H 오전근무 T, 공휴일s, 평일-
				if(ds_MST090PF.GetColumn(0,"PRCMK")!="T"){
					alert(setPopMessage("4706")); //[알림]작업코드 29는 토요일만사용가능(오전근무)합니다.
					setMessage("4706");	
