				sArgument  += " COMPS=" + quote(ComboBoxPlant.Value); //사업부);
				sArgument  += " JOBCD=" + quote(varNewValue);
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync = false;
			if (ds_FHD050PF.GetRowCount() < 1){
				alert(setPopMessage("4063")); //[확인] 작업코드가 존재하지 않습니다.
				setMessage("4063");	
				return false;
			}
			WRKTM=ds_FHD050PF.GetColumn(0,"A3");
			WRKNM=ds_FHD050PF.GetColumn(0,"A2");
			STRTM=ds_FHD050PF.GetColumn(0,"STRTM");
			ENDTM=ds_FHD050PF.GetColumn(0,"ENDTM");
			WRKHM=ds_FHD050PF.GetColumn(0,"JOBHM");
		}
	}
	if (strColumnID == "SWKCD"){	//잔업코드	
		if(varNewValue!=""){

			http.Sync = true;
			var sKind		= "getOVERTIME";		
			var sMethodName = "service::pscomlib/selectOVERTIMEPopup.do"; 
			var sInDataSet  = "";
			var sOutDataSet = "ds_OVERTIM=ds_Select";
