				alert(setPopMessage("4698")); //[알림]지원부서코드가 부서코드와 동일합니다.
				setMessage("4698");	
				return false;
			}
		}
	}
	//지원시간은 지원부서 입력후 가능함.
	if (strColumnID == "SPJTM" || strColumnID == "SPYTM"){//지원시간(주)//지원시간(야)
		if(varNewValue!=""){
			var vChk=ds_FHDC610.GetColumn(nRow, "SUPDP");
			if(length(vChk)<1){
				if(varNewValue!=0){
				alert(setPopMessage("4699")); //[알림]지원부서코드 입력 후 입력가능합니다.
				setMessage("4699");	
				return false;
				}
			}
		}
	}
	/*
	if (strColumnID == "SPYTM"){
		if(varNewValue!=""){
			var vChk=ds_FHDC610.GetColumn(nRow, "SUPDP");
			if(length(vChk)<1){
				if(varNewValue!=0){
				alert("지원부서코드 입력 후 입력가능합니다.");
				return false;
				}
			}
		}
	}	
	*/
	http.usewaitcursor = true;
}

function ds_FHDC610_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "PESABN"){
		if(varNewValue!=""){
			ds_FHDC610.SetColumn(nRow, "KORNM", KORNM); // 거래처명 설정
			ds_FHDC610.SetColumn(nRow, "ORGDP", ORGDP); // 원부서 		
			if(ds_FHD090PF.GetColumn(0,"JOBCD")=="91"){
				alert(setPopMessage("4700")); //[알림]해당사번이 타부서 파견으로 등록되어있습니다.
				setMessage("4700");	
			}
			/*
			if(length(ds_FHDC610.GetColumn(nRow,"WRKCD"))==0){
				if(ds_MST090PF.GetColumn(0,"PRCMK")!="H" && ds_MST090PF.GetColumn(0,"PRCMK")!="S") {
					ds_FHDC610.SetColumn(nRow, "WRKNM", "정상근무"); // 근무명
					ds_FHDC610.SetColumn(nRow, "WRKTM", 0); // 잔업시간I
					ds_FHDC610.SetColumn(nRow, "STRTM", 0); // 시작시간I
					ds_FHDC610.SetColumn(nRow, "ENDTM", 0); // 종료시간I
					ds_FHDC610.SetColumn(nRow, "WRKHM", 0); // 작업시간I					
					ds_FHDC610.SetColumn(nRow, "WEEKMN", WRKTIME); // WRKTM
					ds_FHDC610.SetColumn(nRow, "TMPWEEKMN",0); // 작업일 잔업시간(임시용)	
				}
			//else추가필요	
			}
			//ds_FHDC610.SetColumn(nRow, "WEEKMN", WRKTIME); // WRKTM

		}
		else {
			ds_FHDC610.SetColumn(nRow, "KORNM", ""); // 거래처명 설정
			ds_FHDC610.SetColumn(nRow, "ORGDP", ""); // 원부서 
			ds_FHDC610.SetColumn(nRow, "WEEKMN", ""); // WRKTM
			ds_FHDC610.SetColumn(nRow, "TMPWEEKMN",""); // 작업일 잔업시간(임시용)	
		}
		*/
		if(ds_MST090PF.GetColumn(0,"PRCMK")!="H" && ds_MST090PF.GetColumn(0,"PRCMK")!="S") {
				if(length(ds_FHD090PF.GetColumn(0,"JOBCD"))==0){
				ds_FHDC610.SetColumn(nRow, "WRKCD","");
				ds_FHDC610.SetColumn(nRow, "WRKNM", "정상근무"); // 근무명
				ds_FHDC610.SetColumn(nRow, "WRKTM", 0); // 잔업시간I
				ds_FHDC610.SetColumn(nRow, "STRTM", 0); // 시작시간I
				ds_FHDC610.SetColumn(nRow, "ENDTM", 0); // 종료시간I
				ds_FHDZ110.SetColumn(nRow, "WRKHM", 0); // 작업시간I					
				ds_FHDZ110.SetColumn(nRow, "TMPWEEKMN",0); // 작업일 잔업시간(임시용)	
				}else{
					ds_FHDC610.SetColumn(nRow, "WRKCD", ds_FHD090PF.GetColumn(0,"JOBCD")); // WRKCD
					ds_FHDC610.SetColumn(nRow, "TMPWEEKMN",0); // 작업일 잔업시간(임시용)	
				}
			}else{
				ds_FHDC610.SetColumn(nRow, "WRKCD","");
				ds_FHDC610.SetColumn(nRow, "WRKNM", "정상근무"); // 근무명
				ds_FHDC610.SetColumn(nRow, "WRKTM", 0); // 잔업시간I
				ds_FHDC610.SetColumn(nRow, "STRTM", 0); // 시작시간I
				ds_FHDC610.SetColumn(nRow, "ENDTM", 0); // 종료시간I
				ds_FHDC610.SetColumn(nRow, "WRKHM", 0); // 작업시간I					
			}
			if(length(WRKTIME)==0){
				ds_FHDC610.SetColumn(nRow, "WEEKMN", 0); // WRKTM
			}else{
				ds_FHDC610.SetColumn(nRow, "WEEKMN", WRKTIME); // WRKTM
			}
		}
		else {
			ds_FHDC610.SetColumn(nRow, "KORNM", ""); // 거래처명 설정
			ds_FHDC610.SetColumn(nRow, "ORGDP", ""); // 원부서 
			ds_FHDC610.SetColumn(nRow, "WEEKMN", 0); // WRKTM
			ds_FHDC610.SetColumn(nRow, "TMPWEEKMN",0); // 작업일 잔업시간(임시용)	
			ds_FHDC610.SetColumn(nRow, "WRKCD",""); // 잔업코드초기화
		}	
	}
	if (strColumnID == "WRKCD"){
		if(varNewValue!=""){
			if(ds_MST090PF.GetColumn(0,"PRCMK")!="H" && ds_MST090PF.GetColumn(0,"PRCMK")!="S") {
				ds_FHDC610.SetColumn(nRow, "WRKTM", WRKTM); // 잔업시간I
				ds_FHDC610.SetColumn(nRow, "WRKNM", WRKNM); // 근무명
				ds_FHDC610.SetColumn(nRow, "STRTM", STRTM); // 시작시간I
				ds_FHDC610.SetColumn(nRow, "ENDTM", ENDTM); // 종료시간I
				ds_FHDC610.SetColumn(nRow, "WRKHM", WRKHM); // 작업시간I
				//ds_FHDC610.SetColumn(nRow, "TMPWEEKMN", WRKTM); // 작업일 잔업시간(임시용)
				if(ds_MST090PF.GetColumn(0,"PRCMK")=="-" && ds_FHDC610.GetColumn(nRow,"WRKCD") >= "20" && ds_FHDC610.GetColumn(nRow,"WRKCD") <= "29"){
					ds_FHDC610.SetColumn(nRow, "TMPWEEKMN", (parseInt(WRKTM)-480)/60); // 작업일 잔업시간(임시용)
				}
				else if(ds_MST090PF.GetColumn(0,"PRCMK")=="T" && ds_FHDC610.GetColumn(nRow,"WRKCD") >= "20" && ds_FHDC610.GetColumn(nRow,"WRKCD") <="29"){
					ds_FHDC610.SetColumn(nRow, "TMPWEEKMN", (parseInt(WRKTM)-240)/60); // 작업일 잔업시간(임시용)
				}
				else if((ds_FHDC610.GetColumn(nRow,"WRKCD") >= "10" && ds_FHDC610.GetColumn(nRow,"WRKCD") <="19") ||(ds_FHDC610.GetColumn(nRow,"WRKCD") >= "30" && ds_FHDC610.GetColumn(nRow,"WRKCD") <="89")) {
					ds_FHDC610.SetColumn(nRow, "TMPWEEKMN", parseInt(WRKTM)/60); // 작업일 잔업시간(임시용)
				}else{
					ds_FHDC610.SetColumn(nRow, "TMPWEEKMN",0); // 작업일 잔업시간(임시용)		
				}
			}else{//else추가필요
				ds_FHDC610.SetColumn(nRow, "WRKTM", WRKTM); // 잔업시간I
				ds_FHDC610.SetColumn(nRow, "WRKNM", ""); // 근 무명
				ds_FHDC610.SetColumn(nRow, "STRTM", STRTM); // 시작시간I
				ds_FHDC610.SetColumn(nRow, "ENDTM", ENDTM); // 종료시간I
				ds_FHDC610.SetColumn(nRow, "WRKHM", WRKHM); // 작업시간I
				//ds_FHDC610.SetColumn(nRow, "TMPWEEKMN", WRKTM); // 작업일 잔업시간(임시용)
				if(ds_MST090PF.GetColumn(0,"PRCMK")=="-" && ds_FHDC610.GetColumn(nRow,"WRKCD") >= "20" && ds_FHDC610.GetColumn(nRow,"WRKCD") <= "29"){
					ds_FHDC610.SetColumn(nRow, "TMPWEEKMN", (parseInt(WRKTM)-480)/60); // 작업일 잔업시간(임시용)
				}
				else if(ds_MST090PF.GetColumn(0,"PRCMK")=="T" && ds_FHDC610.GetColumn(nRow,"WRKCD") >= "20" && ds_FHDC610.GetColumn(nRow,"WRKCD") <="29"){
					ds_FHDC610.SetColumn(nRow, "TMPWEEKMN", (parseInt(WRKTM)-240)/60); // 작업일 잔업시간(임시용)
				}
				else if((ds_FHDC610.GetColumn(nRow,"WRKCD") >= "10" && ds_FHDC610.GetColumn(nRow,"WRKCD") <="19") ||(ds_FHDC610.GetColumn(nRow,"WRKCD") >= "30" && ds_FHDC610.GetColumn(nRow,"WRKCD") <="89")) {
					ds_FHDC610.SetColumn(nRow, "TMPWEEKMN", parseInt(WRKTM)/60); // 작업일 잔업시간(임시용)
				}else{
					ds_FHDC610.SetColumn(nRow, "TMPWEEKMN",0); // 작업일 잔업시간(임시용)		
				}
			}
		}
		else {
			ds_FHDC610.SetColumn(nRow, "WRKNM", "정상근무"); // 근무명
			ds_FHDC610.SetColumn(nRow, "WRKTM", 0); // 잔업시간I
			ds_FHDC610.SetColumn(nRow, "STRTM", 0); // 시작시간I
			ds_FHDC610.SetColumn(nRow, "ENDTM", 0); // 종료시간I
			ds_FHDC610.SetColumn(nRow, "WRKHM", 0); // 작업시간I
			ds_FHDC610.SetColumn(nRow, "TMPWEEKMN", 0); // 작업일 잔업시간(임시용)
		}
	}
	if (strColumnID == "SWKCD"){
		if(varNewValue!=""){
			ds_FHDC610.SetColumn(nRow, "SWKCD", SWKCD); // 잔업코드
			ds_FHDC610.SetColumn(nRow, "SWKCR", SWKCR); // 잔업코드명
		}		
		else {
			ds_FHDC610.SetColumn(nRow, "SWKCR", ""); // 잔업코드명
		}
	}		
	if (strColumnID == "SUPDP"){
		if(varNewValue!=""){
			ds_FHDC610.SetColumn(nRow, "SUPDP", SUPDP); // 지원부서
			//ds_FHDC610.SetColumn(nRow, "SPJTM", 0); // 지원시간(주)
			//ds_FHDC610.SetColumn(nRow, "SPYTM", 0); // 지원시간(야)
		}
		else{ //지원부서가 null인경우 지원시간은 0
			ds_FHDC610.SetColumn(nRow, "SUPDP", ""); // 지원부서
			ds_FHDC610.SetColumn(nRow, "SPJTM", 0); // 지원시간(주)
			ds_FHDC610.SetColumn(nRow, "SPYTM", 0); // 지원시간(야)

		}
	}
	http.usewaitcursor = true;
}

function MainGrid_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(ds_FHDC610.GetRowType(nRow)=="insert"){
		if(nCell == 2)
		{
			var sTemp="zzzzzzzzzz";
			var selectnumber = "H6";
			var PAYCD = 2; //1이면 기능직 2이면 직반장
			//var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);
			var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" +  sTemp + " " + "PAYCD=" + PAYCD,584,528);
			if (length(rtn) != 0){
				ds_FHDC610.SetColumn(nRow,"PESABN",rtn[0]);
			}		
		}
	}
	if(nCell == 6)
	{
		var sTemp="zzzzzzzzzz";
		var selectnumber = "K6";
		var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);
		if (length(rtn) != 0){
			ds_FHDC610.SetColumn(nRow, "WRKCD",rtn[0]);// 잔업시간I
		}		
	}
	if(nCell == 9)
	{
		var sTemp="zzzzzzzzzz";
		var selectnumber = "L6";
		var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);
		if (length(rtn) != 0){
			ds_FHDC610.SetColumn(nRow, "SWKCD",rtn[0]);// 잔업시간I
		}		
	}			
	if(nCell == 14)
	{
		var sTemp="zzzzzzzzzz";
		var selectnumber = "F6";
		var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);
		if (length(rtn) != 0){
			ds_FHDC610.SetColumn(nRow, "SUPDP",rtn[0]);// 지원부서	
		}		
	}
	if(nCell == 17)
	{
		ds_POPUP.Copy(ds_FHDC610); //공통팝업사용위함.
		var rtn = dialog("psfaclib::FHDZ110_POPUP.xml","ROWNO=" + nRow,584,528);
		if (length(rtn) != 0){
			var chk1=rtn[5];
			var chk2=rtn[6];
			ds_FHDC610.SetColumn(nRow, "STRTM",rtn[0]);// 시작　시간	
			ds_FHDC610.SetColumn(nRow, "ENDTM",rtn[1]);// 종료	
			ds_FHDC610.SetColumn(nRow, "WRKTM",rtn[2]);// 잔업　시간
			ds_FHDC610.SetColumn(nRow, "WRKHM",rtn[3]);// 작업　시분
			ds_FHDC610.SetColumn(nRow, "SUPDP",rtn[4]);// 지원부서
			ds_FHDC610.SetColumn(nRow, "SPJTM",rtn[5]);// 주간지원시간
			ds_FHDC610.SetColumn(nRow, "SPYTM",rtn[6]);// 야간지원시간
			//지원시간을 null로 변경하였을 경우 지원시간은 0으로 전환
			if(length(chk1)<1){
				ds_FHDC610.SetColumn(nRow, "SPJTM",0);// 주간지원시간	
			}
			if(length(chk2)<1){
				ds_FHDC610.SetColumn(nRow, "SPYTM",0);// 주간지원시간	
			}
		}	
	}		
}

function ds_FHDC610_CanRowPosChange(obj,nOldRow,nRow)
{

	http.usewaitcursor = false;

	var cpesabn=ds_FHDC610.GetColumn(ds_FHDC610.currow,"PESABN"); //사번
	var wrkcd=ds_FHDC610.GetColumn(ds_FHDC610.currow,"WRKCD"); //작업코드
	var supdp=ds_FHDC610.GetColumn(ds_FHDC610.currow,"SUPDP"); //지원부서
	var spjtm=ds_FHDC610.GetColumn(ds_FHDC610.currow,"SPJTM"); //지원시간(주)
	var spytm=ds_FHDC610.GetColumn(ds_FHDC610.currow,"SPYTM"); //지원시간(야)
	var clewtm=ds_FHDC610.GetColumn(ds_FHDC610.currow,"LEWTM"); //조퇴
	var cltwtm=ds_FHDC610.GetColumn(ds_FHDC610.currow,"LTWTM"); //지각
	var cotwtm=ds_FHDC610.GetColumn(ds_FHDC610.currow,"OTWTM"); //외출
	var cswkcd=ds_FHDC610.GetColumn(ds_FHDC610.currow,"SWKCD"); //특근코드
	//var cstrtm=ds_FHDC610.GetColumn(ds_FHDC610.currow,"STRTM"); //시작시간
	//var cendtm=ds_FHDC610.GetColumn(ds_FHDC610.currow,"ENDTM"); //종료시간
	//var cwrkhm=ds_FHDC610.GetColumn(ds_FHDC610.currow,"WRKHM"); //작업시분
	//var cwrktm=ds_FHDC610.GetColumn(ds_FHDC610.currow,"WRKTM"); //잔업시간
	//조,지,외 디폴트 value 0설정
	if(clewtm=""){
		ds_FHDC610.SetColumn(ds_FHDC610.currow,"LEWTM",0);
	}
	if(cltwtm=""){
		ds_FHDC610.SetColumn(ds_FHDC610.currow,"LTWTM",0);
	}
	if(cotwtm=""){
		ds_FHDC610.SetColumn(ds_FHDC610.currow,"OTWTM",0);
	}	
	if(wrkcd>="80"){ //작업코드가 80이상인경우 지원부서 입력불가
		if(length(supdp)!=0){	
			alert(setPopMessage("4701")); //[알림]작업코드가 80이상인경우 지원부서 입력불가능합니다.
			setMessage("4701");	
			return false;
		}
	}
	if(wrkcd>="50" && wrkcd<="59"){ //작업코드가 50이상 59이하인 경우(특근) 조퇴,지각,외출 없슴
		if(clewtm!=0 || cltwtm !=0 || cotwtm !=0){	
			alert(setPopMessage("4702")); //[알림]작업코드가 50이상 59이하인 경우(특근) 조퇴,지각,외출 입력불가능합니다.
			setMessage("4702");	
			//1번. row변경 불가로 할 것인가 2번 0값을 줄것인가.
			//1번
			//ds_FHDC610.SetColumn(ds_FHDC610.currow,"LEWTM",0);
			//ds_FHDC610.SetColumn(ds_FHDC610.currow,"LTWTM",0);
			//ds_FHDC610.SetColumn(ds_FHDC610.currow,"OTWTM",0);
			//2번
			return false;
		}
	}	
	if(wrkcd>="90"){ //작업코드가 90이상인 경우 모든값이 0임.
		ds_FHDC610.SetColumn(ds_FHDC610.currow,"STRTM",0);
		ds_FHDC610.SetColumn(ds_FHDC610.currow,"ENDTM",0);
		ds_FHDC610.SetColumn(ds_FHDC610.currow,"WRKHM",0);
		ds_FHDC610.SetColumn(ds_FHDC610.currow,"WRKTM",0);
		//ds_FHDC610.SetColumn(ds_FHDC610.currow,"SWKCD","");
		//ds_FHDC610.SetColumn(ds_FHDC610.currow,"LEWTM",0);
		//ds_FHDC610.SetColumn(ds_FHDC610.currow,"LTWTM",0);
		//ds_FHDC610.SetColumn(ds_FHDC610.currow,"OTWTM",0);			
		//ds_FHDC610.SetColumn(ds_FHDC610.currow,"SUPDP","");
		//ds_FHDC610.SetColumn(ds_FHDC610.currow,"SPJTM",0);
		//ds_FHDC610.SetColumn(ds_FHDC610.currow,"SPYTM",0);

		if(length(cswkcd) >1 ||clewtm!=0 || cltwtm !=0 || cotwtm !=0||length(supdp) >1 ||spjtm!=0||spytm!=0  ){	
			alert(setPopMessage("4703")); //[알림]작업코드가 90이상인 경우 잔업코드, 조퇴, 지각, 외출, 지원부서, 지원시간 값이 0입니다.
			setMessage("4703");	
		return false;

		}
	}		
	if(length(supdp)!=0){ //지원부서가 null 이 아닌경우 지원시간입력
		if((length(spjtm)==0||spjtm<1)&&(length(spytm)==0||spytm<1)){
			alert(setPopMessage("4707")); //[알림]지원시간을 입력하세요.
			setMessage("4707");	
			return false;
		}
	}
	if(ds_FHDC610.GetRowType(ds_FHDC610.currow)=="update" ||ds_FHDC610.GetRowType(ds_FHDC610.currow)=="insert" ){
		var duChk ="";
		var duVal =0;
		var manChk ="";
		var week12 =0;
		var weekmn =0;
		if(length(ds_FHDC610.GetColumn(ds_FHDC610.currow,"PESABN")) > 1){ 
		//중복체크 -사번, 작업코드(현재의 data확인)
			duChk = parseInt(ds_FHDC610.GetColumn(ds_FHDC610.currow,"PESABN"))+"||"+ds_FHDC610.GetColumn(ds_FHDC610.currow,"WRKCD");
/*
			for(i=0; i <ds_FHDC610.GetRowCount(); i++){
				if(ds_FHDC610.currow==i){
					i++;
				}
				if(duChk == ds_FHDC610.GetColumn(i,"PESABN")+"||"+ds_FHDC610.GetColumn(i,"WRKCD")){
					alert("작업자의 동일한 근무코드가 존재합니다.");
					return false;
				}
			}
*/	
			for(i=0; i <ds_FHDC610.GetRowCount(); i++){
				if(duChk == parseInt(ds_FHDC610.GetColumn(i,"PESABN"))+"||"+ds_FHDC610.GetColumn(i,"WRKCD")){
					duVal++;
				}
				if(duVal > 1){
					alert(setPopMessage("4704")); //[알림]작업자의 동일한 근무코드가 존재합니다.
					setMessage("4704");	
					return false;
				}	
			}

		//중복체크 - 타부서등록여부(FHD110확인)
			http.Sync=true;
			var sKind		= "getDUPBUCHK";						
			var sMethodName = "service::psfaclib/selectFHDC610_BUCHK.do";     		// 서버에서 호출 될 Method 명
			var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
			var sOutDataSet = "ds_BUCHK=ds_BUCHK";    				// 조회 후 돌려 받을 DataSet 명칭
			var sArgument   = "";
				sArgument += " LIB2=" + quote(Lib2);
				sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
				sArgument += " CODE=" + quote(ed_CODE.Text); //부서코드
				sArgument += " SABUN=" + quote(cpesabn); //사번
				sArgument += " WRKDT=" + quote(cmb_WRKDT.Value); //작업일
				//sArgument += " WRKCD=" + quote(cwrkcd); //작업코드
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync=false;
			if (ds_BUCHK.GetRowCount() > 0){
				alert(setPopMessage("4705")); //[알림]타부서에 등록된 작업자입니다.
				setMessage("4705");	
				return false;
			}
		//법정근무시간　주근무시간　주중 12 시간잔업금지
			if(ds_MST090PF.GetColumn(0,"PRCMK")="-" || ds_MST090PF.GetColumn(0,"PRCMK")=="T"){
				weekmn = parseInt(ds_FHDC610.GetColumn(ds_FHDC610.currow,"WEEKMN")); //현작업자의 작업일 이전까지의 누적 잔업시간
				manChk = ds_FHDC610.GetColumn(ds_FHDC610.currow,"PESABN"); //현재작업자
				for(i=0; i <ds_FHDC610.GetRowCount(); i++){
					if(manChk == parseInt(ds_FHDC610.GetColumn(i,"PESABN"))){ //동일작업자가 있으면 작업일의 작업시간 합계
						week12 = parseInt(ds_FHDC610.GetColumn(i,"TMPWEEKMN")); 
						weekmn+=week12;
					}
				}
				if(weekmn > 12){
					alert("사번 "+manChk+"의 작업자의 주근무시간이 "+weekmn+ "시간으로 12시간을 초과합니다.");
					return false;
				}
			}else{
				weekmn = 0; //휴일작업시간 누계
				manChk = ds_FHDC610.GetColumn(ds_FHDC610.currow,"PESABN"); //현재작업자
				for(i=0; i <ds_FHDC610.GetRowCount(); i++){
					if(manChk == parseInt(ds_FHDC610.GetColumn(i,"PESABN"))){ //동일작업자가 있으면 작업일의 작업시간 합계
						week12 = parseInt(ds_FHDC610.GetColumn(i,"TMPWEEKMN")); 
						weekmn+=week12;

					}
				}
				if(weekmn > 8){ //휴일잔업가능시간은 8시간
					alert("사번 "+manChk+"의 작업자의 휴일근무시간이 "+weekmn+ "시간으로 8시간을 초과합니다.");
					return false;
				}			

			}
			return true;
		}
	}
	http.usewaitcursor = true;
}
