}

function fn_Input()
{
	if(!validationChk()){
		return;
	}
	if(checkInsertCount("ds_FHDZ000")== false) {
		return;
	}
	if(ds_FHDZ000.rowcount < 1) {
		fn_Search();
	}
	ds_FHDZ000.AddRow();
	ds_FHDZ000.SetColumn(ds_FHDZ000.currow,"DHCOMP",ComboBoxPlant.Value);
	ds_FHDZ000.SetColumn(ds_FHDZ000.currow,"DHCODE",ed_CODE.Text);
}

function fn_Delete()
{

	if(ds_FHDZ000.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_FHDZ000.DeleteRow(ds_FHDZ000.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
	setMessage("1011");
}

