function imgCODE_OnClick(obj,nX,nY)
{
	var sTemp=trim(ed_CODE.Text);
	if (length(trim(ed_CODE.Text)) == 0) sTemp="zzzzzzzzzz";
	var selectnumber = "F6";
	var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp + " " + "COMPS=" + ComboBoxPlant.Value,584,528);
	if (length(rtn) != 0){
		ed_CODE.Text = rtn[0];
		st_CODE.Text = rtn[1];
	}		
}

function ds_FHDZ000_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "PROCS"){//일자
		if(varNewValue!=""){
			if(ds_FHDZ000.FindRow("PROCS", varNewValue) >= 0){
				//alert("[경고] 이미 등록된 자료입니다.");  //[경고] 이미 등록된 자료입니다.
				alert(setPopMessage("1520"));  //[경고] 이미 등록된 자료입니다.
				setMessage("1520");	
				return false;
			}else{
				var sKind		= "getBM001PR";						
				var sMethodName = "service::pscomlib/selectBM001PR.do";    // 서버에서 호출 될 Method 명
				var sInDataSet  = "";               //자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "ds_BM001PR=ds_BM001PR";  	   // 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
					sArgument += " LIB1=" + quote(Lib1);
					sArgument += " PROCS=" + quote(varNewValue); //공정코드
				http.Sync=true;
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
				http.Sync=false;
				http.usewaitcursor = true;
				if(ds_BM001PR.rowcount<1){
					//alert("[알림] PROCESS MA에 미등록된 공정코드입니다.");
					alert(setPopMessage("4746"));//[알림] PROCESS MA에 미등록된 공정코드입니다.
					setMessage("4746");
					return false;
				}

			}
		}
	}
	if (strColumnID == "PRONM"){//참조사항 글자수계산
		if(varNewValue!=""){//FHD000PF AND MST650PF 한번에 처리함.(확인됨)
			var chkLeng = array(2);
				chkLeng = lengCal(varNewValue);
			if(chkLeng[1] == 18){
				//alert("[알림]참조사항 입력길이를 초과하였습니다."); //[알림]참조사항 입력길이를 초과하였습니다.
				alert(setPopMessage("6097")); //[알림]참조사항 입력길이를 초과하였습니다.
				setMessage("6097");	
				return false;
			}
		}
	}	
	http.usewaitcursor = true;
}

//글자길이계산 메소드
function lengCal(input)
{
	var availMsg = 0;//이전DATA TYPE
	var chkbyte = 0; //byte(길이계산
	var calj = 0; //실제length 계산이 한글과기타문자사이에는 길이 1+함,
	var msgVal = input;
	var rval = array(2);
	for(var i = 0 ; i < msgVal.length; i++){
		var ch =Asc(msgVal.CharAt(i));
		if(i==msgVal.length-1){//ex)'가'입력시 4byte
			if(i==0){//한글자입력시
				if( length(urlEncode(ch)) > 4){
					chkbyte +=4;//한글4
					calj+=2;
				}else{
					chkbyte++;//기타1
				}	
			}else{//이전에 값이 있는경우
				if(length(availMsg)>4){//이전데이터가 한글
					if( length(urlEncode(ch)) > 4){//한글2
						chkbyte +=3;
						calj++;
					}else{//기타1+1
						chkbyte+=2;
					}				
				}else{
					if( length(urlEncode(ch)) > 4){//한글2
						chkbyte +=3;
						calj+=2;
					}else{//기타1
						chkbyte+=1;
					}					
				}
			}					
		}else{
			if(i==0){//처음에 값이 없는 경우
				if( length(urlEncode(ch)) > 4){
					chkbyte +=3;
					calj++;
				}else{
					chkbyte++;
				}	
			}else{//값이 있는경우
				if(length(availMsg)>4){//이전데이터가 한글
					if( length(urlEncode(ch)) > 4){//한글2
						chkbyte+=2;				
					}else{//기타1
						chkbyte+=2;				
						calj++;
					}	
				}else{
					if( length(urlEncode(ch)) > 4){//한글2
						chkbyte +=3;
						calj++;
					}else{//기타1
						chkbyte+=1;
					}					
				}
			}			
		}
		availMsg=urlEncode(ch);

		if(chkbyte>=18){
			rval[0]=i;
			//rval[1]=i+1+calj;
			rval[1]=chkbyte;
			return rval;
		}
	}
	return rval;
}
