var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("2.2"); //조회, 추가, 저장
	getComCode(); //공장구분 선택
	//ed_PESABN.Text="411006";
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	//MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

}

function getFHDZ010(){

	if(!validationChk()){
		return;
	}

	var sKind		= "getFHDZ010";						
	var sMethodName = "service::psfaclib/selectFHDZ010.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FHDZ010=ds_FHDZ010";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@PESABN=" + quote(ed_PESABN.Text); //용역번호
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
		Div0.imgCODE.Enable=true;
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
		Div0.imgCODE.Enable=false;
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/  
function setFHDZ010(){

	if(!validationChk()){
		return;
	}
	if(ds_FHDZ010.GetRowType(ds_FHDZ010.currow)=="update" ||ds_FHDZ010.GetRowType(ds_FHDZ010.currow)=="insert" ){
		if(!setValidationChk()){
			return;
		}
	}

	var sKind		= "setFHDZ010";						
	var sMethodName = "service::psfaclib/saveFHDZ010.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_FHDZ010=ds_FHDZ010:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FHDZ010_CHECK=ds_FHDZ010_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_FHDZ010_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_FHDZ010_CHECK.GetRowCount();i++){
			alert("용역번호 : " +ds_FHDZ010_CHECK.GetColumn(i,"PESABN") + " = " + ds_FHDZ010_CHECK.GetColumn(i,"RTMSG"));
		}
		getFHDZ010();  //error항목이 존재시에는 재조회
		return;
	}// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
		ed_PESABN.BKColor="white";

	if (length(trim(ed_PESABN.Text))==0){
		ed_PESABN.BKColor="paleturquoise";
		//alert("[알림]용역번호를 입력하세요.");
		alert(setPopMessage("6103"));//[알림]용역번호를 입력하세요.
		setMessage("6103");
		result = false;
	}else{//인사마스터 사번과 중복여부확인
		http.Sync = true;
		var sKind		= "getHMD020PF";		
		var sMethodName = "service::pscomlib/selectSABUN.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_HMD020PF=ds_HMD020PF";
		var sArgument   = "";
			sArgument  += " LIB3=" + quote(Lib3);
			sArgument  += " SABUN=" + quote(ed_PESABN.Text);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if(ds_HMD020PF.rowcount>0){
			//alert("[알림]회사 인사마스터에 등록된 사번과 중복됩니다.");
			alert(setPopMessage("6104"));//[알림]회사 인사마스터에 등록된 사번과 중복됩니다.
			setMessage("6104");
			result = false;
		}
	}
	return result;
}

function setValidationChk(){
	var result = true;
		Div0.ed_DHCODE.BKColor="white";
		Div0.cmb_STRDT.BKColor="white";
		Div0.cmb_ENDDT.BKColor="white";
		Div0.ed_KORNM.BKColor="white";

	if (length(trim(Div0.ed_DHCODE.Text))==0){
		Div0.ed_DHCODE.BKColor="paleturquoise";
		//alert("[알림]작업반을 입력하세요.");
		alert(setPopMessage("6080"));//[알림]작업반을 입력하세요.
		setMessage("6080");
		result = false;
	}else if (Div0.cmb_STRDT.Value==""){
		Div0.cmb_STRDT.BKColor="paleturquoise";
		//alert("[알림]시작일자를 입력하세요.");
		alert(setPopMessage("6025"));//[알림]시작일자를 입력하세요.
		setMessage("6025");
		result = false;
	}else if (Div0.cmb_ENDDT.Value==""){
		Div0.cmb_ENDDT.BKColor="paleturquoise";
		//alert("[알림]종료일자를 입력하세요.");
		alert(setPopMessage("6026"));//[알림]종료일자를 입력하세요.
		setMessage("6026");
		result = false;
	}else if (Div0.cmb_STRDT.Value > Div0.cmb_ENDDT.Value){
		Div0.cmb_STRDT.BKColor="paleturquoise";
		//alert("[확인] 시작일자가 종료일자보다 클 수는 없습니다.");
		alert(setPopMessage("1503"));//[확인] 시작일자가 종료일자보다 클 수는 없습니다.
		setMessage("1503");
		result = false;
	}else if (length(trim(Div0.ed_KORNM.Text)) == 0){
		Div0.ed_KORNM.BKColor="paleturquoise";
		//alert("[알림]성명을 입력해 주세요.");
		alert(setPopMessage("6105"));//[알림]성명을 입력해 주세요.
		setMessage("6105");
		result = false;
	}
	return result;
}

