function fn_Search()
{
	getFHDZ010();
}

function fn_Input()
{
	if(!validationChk()){
		return;
	}
	if(checkInsertCount("ds_FHDZ010")== false) {
		return;
	}
	if(ds_FHDZ010.rowcount < 1) {
		fn_Search();
	}
	if(ds_FHDZ010.rowcount = 1) {
		//alert("[경고] 이미 등록된 자료입니다.");
		alert(setPopMessage("1520"));//[경고] 이미 등록된 자료입니다.
		setMessage("1520");		
		return;
	}	
	ds_FHDZ010.AddRow();
	ds_FHDZ010.SetColumn(ds_FHDZ010.currow,"DHCOMP",ComboBoxPlant.Value);
	ds_FHDZ010.SetColumn(ds_FHDZ010.currow,"PESABN",ed_PESABN.Text);
	Div0.imgCODE.Enable=true;
	Div0.ed_DHCODE.Enable=true;
}

function fn_Delete()
{

	if(ds_FHDZ010.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_FHDZ010.DeleteRow(ds_FHDZ010.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
	setMessage("1011");
}

