//용역테이블FHD010PF 조회($CALL1)
function imgOUTEMP_OnClick(obj,nX,nY)
{
	var sTemp="zzzzzzzzzz";
	var sSabun=ed_PESABN.Text;
	var rtn = dialog("Common::smanPopup.xml", "COMP=" + ComboBoxPlant.Value + " " +"CODE=" + sTemp+ " " +"PESABN=" + sSabun,584,528);
	if (length(rtn) != 0){
