		st_KORNM.Text = rtn[1];
		ds_FHDZ010.ClearData();
	}	
}

function imgCODE_OnClick(obj,nX,nY)
{
	var sTemp=trim(Div0.ed_DHCODE.Text);
	if (length(trim(Div0.ed_DHCODE.Text)) == 0) sTemp="zzzzzzzzzz";
	var selectnumber = "F6";
	var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);
	if (length(rtn) != 0){
		Div0.ed_DHCODE.Text = rtn[0];
		Div0.st_DHCODE.Text = rtn[1];
	}		
}

function ed_DHCODE_OnCharChanged(obj,strPreText,strPostText)
{
	Div0.st_DHCODE.Text = "";	
}

function ed_DHCODE_OnKillFocus(obj)
{
	if(length(trim(Div0.ed_DHCODE.Text)) < 1) return;
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getDPARTMST";		
	var sMethodName = "service::pscomlib/selectDPARTMasterPopup.do"; //FHD00PF조회+MST650PF
	var sInDataSet  = "";
	var sOutDataSet = "ds_DPART=ds_Select";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " COMPS=" + quote(Div0.ComboBoxPlant.Value);
		sArgument  += " DPCODE=" + quote(Div0.ed_DHCODE.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_DPART.GetRowCount() < 1){
		alert(setPopMessage("1002"));
		setMessage("1002");	
		Div0.ed_DHCODE.SetFocus();
		return;
	}
	Div0.st_DHCODE.Text=ds_DPART.GetColumn(0,"A2");
	http.usewaitcursor = true;	
}

//글자길이계산 메소드
function Div0_ed_KORNM_OnKillFocus(obj)
{
	var availMsg = 0;//이전DATA TYPE
	var chkbyte = 0; //byte(길이계산
	var calj = 0; //실제length 계산이 한글과기타문자사이에는 길이 1+함,
	var msgVal = Div0.ed_KORNM.Text;
	var rval = array(2);
	for(var i = 0 ; i < msgVal.length; i++){
		var ch =Asc(msgVal.CharAt(i));
		if(i==msgVal.length-1){//ex)'가'입력시 4byte
			if(i==0){//한글자입력시
				if( length(urlEncode(ch)) > 4){
					chkbyte +=4;//한글4
					calj+=2;
				}else{
					chkbyte++;//기타1
				}	
			}else{//이전에 값이 있는경우
				if(length(availMsg)>4){//이전데이터가 한글
					if( length(urlEncode(ch)) > 4){//한글2
						chkbyte +=3;
						calj++;
					}else{//기타1+1
						chkbyte+=2;
					}				
				}else{
					if( length(urlEncode(ch)) > 4){//한글2
						chkbyte +=3;
						calj+=2;
					}else{//기타1
						chkbyte+=1;
					}					
				}
			}					
		}else{
			if(i==0){//처음에 값이 없는 경우
				if( length(urlEncode(ch)) > 4){
					chkbyte +=3;
					calj++;
				}else{
					chkbyte++;
				}	
			}else{//값이 있는경우
				if(length(availMsg)>4){//이전데이터가 한글
					if( length(urlEncode(ch)) > 4){//한글2
						chkbyte+=2;				
					}else{//기타1
						chkbyte+=2;				
						calj++;
					}	
				}else{
					if( length(urlEncode(ch)) > 4){//한글2
						chkbyte +=3;
						calj++;
					}else{//기타1
						chkbyte+=1;
					}					
				}
			}			
		}
		availMsg=urlEncode(ch);

		if(chkbyte>=13){
			//alert("[알림] 입력범위를 초과했습니다. 초과된 부분은 삭제합니다.");
			alert(setPopMessage("2561"));//[알림] 입력범위를 초과했습니다. 초과된 부분은 삭제합니다.
			setMessage("2561");
			Div0.ed_KORNM.Text=substr(msgVal,0,i);
			return;
		}
	}
}

