{
	var fName   = "생산일일근태조회(마감전)";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|생산일일근태조회(마감전)";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

