function imgCODE_OnClick(obj,nX,nY)
{
	var sTemp=trim(ed_CODE.Text);
	if (length(trim(ed_CODE.Text)) == 0) sTemp="zzzzzzzzzz";
	var selectnumber = "F6";
	var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);
	if (length(rtn) != 0){
		ed_CODE.Text = rtn[0];
		st_CODE.Text = rtn[1];
	}		
}

function ed_CODE_OnCharChanged(obj,strPreText,strPostText)
{
	st_CODE.Text = "";		
}

//MST650과FHD000PF 한번에 처리함.
function ed_CODE_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	//득의선Check
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getDPARTMST";		
	var sMethodName = "service::pscomlib/selectDPARTMasterPopup.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_DPART=ds_Select";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument  += " DPCODE=" + quote(ed_CODE.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_DPART.GetRowCount() < 1){
		alert(setPopMessage("4708"));
		setMessage("4708");	
		ed_CODE.SetFocus();
		return;
	}
	st_CODE.Text=ds_DPART.GetColumn(0,"A2");
	http.usewaitcursor = true;		
}

function cmb_WRKDT_OnKillFocus(obj)
{
	if(length(trim(cmb_WRKDT.Value)) < 1) return;
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getMST090PF";		
	var sMethodName = "service::pscomlib/selectMST090PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST090PF=ds_MST090PF";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " PRCDT=" + quote(cmb_WRKDT.Value); //작업일자
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_MST090PF.GetRowCount() < 1){
		alert(setPopMessage("4061"));
		setMessage("4061");	
		cmb_WRKDT.SetFocus();
		return;
	}

	//법정근로시간 : 달력마스터에 근무일이 등록되어 있으면 법정근로시간 확인
	else{
		//http.Sync 	= true;
		var sKind		= "getWRKTM";		
		var sMethodName = "service::psfaclib/selectMST090_WRKTM.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_WRKTM=ds_WRKTM";
		var sArgument   = "";
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument += " PRCDT=" + quote(cmb_WRKDT.Value); //작업일자
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	}
		//http.Sync 	= false;
	http.usewaitcursor = true;
}

