function setFHDZ210(){

	if(!validationChk()){
		return;
	}
	var sKind		= "setFHDZ210";						
	var sMethodName = "service::psfaclib/saveFHDZ210.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_FHDZ210_FHD210=ds_FHDZ210_FHD210:U ds_FHDZ210_FHD310=ds_FHDZ210_FHD310:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FHDZ210_CHECK=ds_FHDZ210_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		//sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " DHCODE=" + quote(ed_CODE.Text); //부서코드
		sArgument += " WRKDT=" + quote(cmb_WRKDT.Value); //작업일자
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
/*
	if (ds_FHDZ210_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_FHDZ210_CHECK.GetRowCount();i++){
			alert("코드 : " +ds_FHDZ210_CHECK.GetColumn(i,"MGMCD") +" 코드명 : " +ds_FHDZ210_CHECK.GetColumn(i,"MGMNM") + " = " + ds_FMSZ110_CHECK.GetColumn(i,"RTMSG"));
		}
		getFHDZ210();  //error항목이 존재시에는 재조회
		return;
	}// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
*/
	if(svcId=="getFHDZ210"){
		if(rtnCd<0){// tran 실패
			alert(setPopMessage("1002"));
			setMessage("1002");
		// 조회건수가 없을경우
		} else if(ds_FHDZ210_FHD110.rowcount<1){
			//alert("조회된거 없다!");
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			//alert("조회됐다.!");
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
	}
	else if(svcId=="setFHDZ210"){
		if (ds_FHDZ210_CHECK.rowcount > 0){
			for(var i=0;i<ds_FHDZ210_CHECK.GetRowCount();i++){
				alert("코드 : " +ds_FHDZ210_CHECK.GetColumn(i,"MGMCD") +" 코드명 : " +ds_FHDZ210_CHECK.GetColumn(i,"MGMNM") + " = " + ds_FHDZ210_CHECK.GetColumn(i,"RTMSG"));
			}
			getFHDZ210();  //error항목이 존재시에는 재조회
			return;
		}// tran 실패
		if(rtnCd<0){
			alert(setPopMessage("1002"));
			setMessage("1002");
		}else{
			alert(setPopMessage("1001"));
			setMessage("1001");	
		}
	}
}


/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	ed_CODE.BKColor="white";
	cmb_WRKDT.BKColor="white";

	if(length(trim(st_CODE.Text)) == 0){
		ed_CODE.BKColor="paleturquoise";
		alert(setPopMessage("6080"));//[알림]작업반을 입력하세요.
		setMessage("6080");
		result = false;
	}else if(cmb_WRKDT.Value == ""){
		cmb_WRKDT.BKColor="paleturquoise";
		alert(setPopMessage("6075"));//[알림]작업일자를 입력하세요.
		setMessage("6075");
		result = false;
	}
	return result;
}

