	ds_FHDZ210_FHD310.ClearData();
	ds_FHDZ210_FHD110.ClearData();
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "작업일보 총 작업시간";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid1|제외시간";
	arrSheets[1]  = "MainGrid2|관리시간";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);	
}

