{
	st_CODE.Text = "";	
}

//FHD000PF AND MST650PF 한번에 처리함.(확인됨)
function ed_CODE_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	//득의선Check
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getDPARTMST";		
	var sMethodName = "service::pscomlib/selectDPARTMasterPopup.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_DPART=ds_Select";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument  += " DPCODE=" + quote(ed_CODE.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_DPART.GetRowCount() < 1){
		alert(setPopMessage("1002"));
		setMessage("1002");	
		ed_CODE.SetFocus();
		return;
	}
	st_CODE.Text=ds_DPART.GetColumn(0,"A2");
	http.usewaitcursor = true;	
}

function imgCODE_OnClick(obj,nX,nY)
{
	var sTemp=trim(ed_CODE.Text);
	if (length(trim(ed_CODE.Text)) == 0) sTemp="zzzzzzzzzz";
	var selectnumber = "F6";
	var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);
	if (length(rtn) != 0){
		ed_CODE.Text = rtn[0];
		st_CODE.Text = rtn[1];
	}		
}

function ds_FHDZ210_FHD210_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "EXJMN"){
		if(varNewValue == ""){
			ds_FHDZ210_FHD210.SetColumn(nRow,"EXJMN",0);
		}
	}
	if(strColumnID == "EXJTM"){
		if(varNewValue == ""){
			ds_FHDZ210_FHD210.SetColumn(nRow,"EXJTM",0);
		}
	}
	if(strColumnID == "EXYMN"){
		if(varNewValue == ""){
			ds_FHDZ210_FHD210.SetColumn(nRow,"EXYMN",0);
		}
	}
	if(strColumnID == "EXYTM"){
		if(varNewValue == ""){
			ds_FHDZ210_FHD210.SetColumn(nRow,"EXYTM",0);
		}
	}
}

function ds_FHDZ210_FHD310_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "ADJMN"){
		if(varNewValue == ""){
			ds_FHDZ210_FHD310.SetColumn(nRow,"ADJMN",0);
		}
	}
	if(strColumnID == "ADJTM"){
		if(varNewValue == ""){
			ds_FHDZ210_FHD310.SetColumn(nRow,"ADJTM",0);
		}
	}
	if(strColumnID == "ADYMN"){
		if(varNewValue == ""){
			ds_FHDZ210_FHD310.SetColumn(nRow,"ADYMN",0);
		}
	}
	if(strColumnID == "ADYTM"){
		if(varNewValue == ""){
			ds_FHDZ210_FHD310.SetColumn(nRow,"ADYTM",0);
		}
	}
}
function btn_DEL210(){
		var rownm=ds_FHDZ210_FHD210.currow;
		ds_FHDZ210_FHD210.SetColumn(rownm,"EXJMN",0);
		ds_FHDZ210_FHD210.SetColumn(rownm,"EXJTM",0);
		ds_FHDZ210_FHD210.SetColumn(rownm,"EXYMN",0);
		ds_FHDZ210_FHD210.SetColumn(rownm,"EXYTM",0);
}
function btn_DEL310(){
		var rownm=ds_FHDZ210_FHD310.currow;
		ds_FHDZ210_FHD310.SetColumn(rownm,"ADJMN",0);
