var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

var next_month;

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("1"); //조회, 추가, 저장
	getComCode(); //공장구분 선택

}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

	//Static0.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
	//Static3.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
}

function getFMZE101Q(){

	if(!validationChk()){
		return;
	}

	var p_progr2,p_proln2, p_pspno2;
	p_progr2 = iif (length(trim(ed_PROGR_T.Text)) == 0, '999', trim(ed_PROGR_T.Text));
	p_proln2 = iif (length(trim(ed_PROLN_T.Text)) == 0 , '99', trim(ed_PROLN_T.Text));
	p_pspno2 = iif(length(trim(ed_PSPNO_T.Text)) == 0 , '999999999999999', trim(ed_PSPNO_T.Text));

http.TimeOut=900;	
	var sKind		= "getFMZE101Q";						
	var sMethodName = "service::psfaclib/selectFMZE101Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FMZE101Q=ds_FMZE101Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@TRNDTF=" + quote(cmb_TRNDT_F.Value); //실적일자FR
		sArgument += "@TRNDTT=" + quote(cmb_TRNDT_T.Value); //실적일자TO
		sArgument += "@PROGRF=" + quote(ed_PROGR_F.Text); //공정그룹FR
		sArgument += "@PROGRT=" + quote(p_progr2); //공정그룹TO
		sArgument += "@PROLNF=" + quote(ed_PROLN_F.Text); //공정그룹FR
		sArgument += "@PROLNT=" + quote(p_proln2); //공정그룹TO
		sArgument += "@PSPNOF=" + quote(ed_PSPNO_F.Text); //공정그룹FR
		sArgument += "@PSPNOT=" + quote(p_pspno2); //공정그룹TO
		sArgument += "@ROLGB=" + quote(cmb_ROLGB.Value); //내외구분	
		//sArgument += "@PLMYM=" + quote(substr(cmb_PLMYM.Value,0,6)); //계획년월
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
http.TimeOut=300;
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	cmb_TRNDT_F.BKColor="white";
	cmb_TRNDT_T.BKColor="white";

	if(cmb_TRNDT_F.Value == ""){
		cmb_TRNDT_F.BKColor="paleturquoise";
		alert(setPopMessage("6062"));//[알림]실적일자를 입력하세요.
		setMessage("6062");
		result = false;
	}else if(cmb_TRNDT_T.Value == ""){
		cmb_TRNDT_T.BKColor="paleturquoise";
		alert(setPopMessage("6062"));//[알림]실적일자를 입력하세요.
		setMessage("6062");
		result = false;
	}else if(cmb_TRNDT_F.Value > cmb_TRNDT_T.Value){
		cmb_TRNDT_F.BKColor="paleturquoise";
		alert(setPopMessage("6063"));//[알림]실적일자를 확인하세요.
		setMessage("6063");
		result = false;
	}
	return result;
}
