function getComCode(){

	if (comps == "E1"){
		Div0.cb_searchCodeName1.Value="PE";
	}else{
		Div0.cb_searchCodeName1.Value="PS";
	}
}

function getMst040()
{
	var sKind		= "getMST040PF";						
	var sMethodName = "service::pscomlib/selectMST040.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MST040PF=ds_MST040PF";   					// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(Div0.cb_searchCodeName1.Value);
	sArgument += " MAJCD=" + quote("FPY220PF"+Div0.cmb_ACPL.Value + Div0.cmb_ACGB.Value);
	http.Sync 		= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 		= false;
}

/********************************************************************** 
 * 내용설명 : 마감
 * 주요변수 : 
 **********************************************************************/ 
function setFPMC130C(){

	if(length(trim(Div0.cmb_ACYM.Value)) < 8){
		alert(setPopMessage("4053"));  //[확인] 기준년월을 확인바랍니다.
		setMessage("4053");	
		Div0.cmb_ACYM.SetFocus();
		return;
	}
	if(Div0.cmb_ACPL.Value="M0" && Div0.cmb_ACGB.Value!=0){
		//alert("[알림]작업CD가 실적인 경우 기준년월처리만 선택가능합니다.");
		alert(setPopMessage("6054"));  //[알림]작업CD가 실적인 경우 기준년월처리만 선택가능합니다.
		setMessage("6054");	
		Div0.cmb_ACPL.SetFocus();
		return;
	}

//추후삭제
	if(Div0.cmb_ACPL.Value="M2"){
		//alert("[알림]MST040PF갱신안함..확인필요");
		alert(setPopMessage("6054"));  //[알림]작업CD가 실적인 경우 기준년월처리만 선택가능합니다.
		setMessage("6054");	
		Div0.cmb_ACPL.SetFocus();
		return;
	}

	getMst040();
	var magYM = substr(Div0.cmb_ACYM.Value,0,6);
	if(magYM <= ds_MST040PF.GetColumn(0,"MJINT")){
		//alert("[경고] 전월에 산출작업이 미완료 되었거나 이미 산출된 년월입니다.");
		alert(setPopMessage("5012"));  //[경고] 전월에 산출작업이 미완료 되었거나 이미 산출된 년월입니다.
		setMessage("5012");	
		Div0.cmb_ACPL.SetFocus();
		return;
	}
	//실제처리년월계산
	var acpm = addMonth(Div0.cmb_ACYM.Value, Div0.cmb_ACGB.Value);
//	alert(acpm);

	if(confirm(substr(Div0.cmb_ACYM.Value,0,4)+"년 "+substr(Div0.cmb_ACYM.Value,4,2)+"월"+" "+Div0.cmb_ACPL.Text+" "+ Div0.cmb_ACGB.Text
	 + " 월간 생산계획 공수분석 생성 작업입니다. 작업하시겠습니까? ") == false) return;

	st_Msg.Visible = true;
	Div0.Enable = false;
	SetWaitCursor(true);
	SetCapture();

	/* 삭제 : 2019.11.28 NEWDIONE로 SSO관리전환
	//as400 USERID Get
	http.Sync = true;
	var sKind		= "getSsoAccount";						
	var sMethodName = "service::user/selectSsoAccount.do";   	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ssoaccount=ds_ssoaccount";    		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " USER_ID="    + quote(G_ds_user.GetColumn(0,"USER_ID"));
	sArgument += " INOUT_GUBN=" + quote("I");	
	sArgument += " COMPANY="    + quote(company);
	sArgument += " SYSTEM_SEQ=" + quote("4");
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_ssoaccount.GetRowCount() < 1){
		alert(setPopMessage("1014"));
		setMessage("1014");	
		return;
	}	
	*/
	//사업부별 공통ID(as400 USERID)로 실행
	var iDget = getRemotecallID(comps);
	if (iDget != "ERROR"){
		callUser = iDget;
		callPass = iDget;
	}else{
		alert(setPopMessage("1444"));
		setMessage("1444");	
		return;
	}

	//리모트콜 실행(FPYZ209rc용)
	param = Div0.cb_searchCodeName1.Value + Div0.cmb_ACPL.Value +  substr(Div0.cmb_ACYM.Value,0,6) + Div0.cmb_ACGB.Value + substr(acpm,0,6);
	for(i=length(param)+1;i<=40;i++){
		param += " ";
	}
	param += callUser;		
	for(j=1;j<=10-length(callUser);j++){
		param += " ";
	}
	param += '000';

	st_Msg.Visible = true;
	Div0.Enable = false;
	SetWaitCursor(true);
	SetCapture();

	WTA_RemoteCall0.RC_SetAttrValue(0,"2984",exeTime);	

	// 연결 (접속아이피 , 아이디, 비밀번호)	
	var rtn = WTA_RemoteCall0.RC_Connect(callIp,callUser,callPass);

	//접속성공시 
	if(rtn != 0){
		//프로그램 Call (라이브러리명, 프로그램명, 인자값, 크기);
		var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall(pgmLib,pgmName,param,pgmSize);
		//정상적으로 수행했을 시
		if(rtn2 != 0){
			ReleaseCapture();
			SetWaitCursor(false);
			st_Msg.Visible = false;
			if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "999") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				//Div0.Enable = true;
				/*
				var sKind		= "setmst040";						
				var sMethodName = "service::pscomlib/updateMST040_PSDIOLIB.do";     		// 서버에서 호출 될 Method 명
				//var sMethodName = "service::pscomlib/updateMST040.do";     		// 서버에서 호출 될 Method 명
				var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "";    		 								// 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
					sArgument += " LIB2=" + quote(Lib2);
					sArgument += " COMPS=" + quote(Div0.cb_searchCodeName1.Value);//사업부
					sArgument += " MAJCD=" + quote("FPY220PF"+Div0.cmb_ACPL.Value + Div0.cmb_ACGB.Value);
					sArgument += " MJINT=" + quote(substr(Div0.cmb_ACYM.Value,0,6));//MJINT
					sArgument += " MAJMK=" + quote(G_ds_user.GetColumn(0,"USER_ID"));//MAJMK
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
				*/
				//마감history에 저장
				var sKind		= "setmst049";						
				var sMethodName = "service::pscomlib/saveMST049.do";     		// 서버에서 호출 될 Method 명
				var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "";    		 								// 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += " LIB2=" + quote(Lib2);
				sArgument += " COMPS=" + quote(Div0.cb_searchCodeName1.Value);
				sArgument += " PGSRC=" + quote("FPMC130C");
				sArgument += " PGMNM=" + quote("월간생산계획 공수분석작업");
				sArgument += " PDATE=" + quote(substr(Div0.cmb_ACYM.Value,0,6));
				sArgument += " JOBID=" + quote(G_ds_user.GetColumn(0,"USER_ID"));
				sArgument += " BIGO1=" + quote("FPY220PF"+Div0.cmb_ACPL.Value + Div0.cmb_ACGB.Value);
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

				alert(setPopMessage("1001")); //  정상적으로 처리가 완료되었습니다.	
				setMessage("1001");

			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "888") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				Div0.Enable = true;
				var ruser = trim(substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,10));
				var rcount = toNumber(substr(WTA_RemoteCall0.RC_GetReturnParm(1),60,2))-1;
				alert("사용자("+ruser+")외 "+rcount+"명이 사용중입니다. 작업이 불가능 합니다.");
				//alert(setPopMessage("1008"));	// 현재 시스템에 락이 걸렸습니다. 전산관리자에게 문의하세요.
				//setMessage("1008");
				return false;
			} else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "777") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				Div0.Enable = true;
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return false;
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "666") {
				alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				Div0.Enable = true;
				alert(setPopMessage("4020")); //[확인] 마감년월을 확인바랍니다.
				setMessage("4020");	
				return false;
			}else {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				Div0.Enable = true;
				alert(setPopMessage("1015")); //[경고] AS400 CL 처리 중 오류가 발생하였습니다.
				setMessage("1015");	
				return false;
			}
		}else{
			var bstrErrorMessage = WTA_RemoteCall0.bstrErrorMessage;
			WTA_RemoteCall0.RC_Disconnect();	//연결종료
			ReleaseCapture();
			SetWaitCursor(false);
			//오류 코드 경고창
			alert("에러 메세지=======>" + bstrErrorMessage);
		}
	//접속실패시
	}else{
		var ulErrorCode = WTA_RemoteCall0.ulErrorCode;
		WTA_RemoteCall0.RC_Disconnect();	//연결종료
		ReleaseCapture();
		SetWaitCursor(false);
		//오류 코드 경고창
		alert("접속 실패 오류=======>" + ulErrorCode);
	}
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- svcId	Transaction 고유 ID
 * 			- rtnCd 	에러코드
 * 			- rtnMsg 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if(rtnCd<0){
		ReleaseCapture();
		SetWaitCursor(false);
		alert(setPopMessage("5008"));  //[오류] 마감처리는 진행되었으나 마감년월을 관리마스타(MST040PF)에 저장 실패. 
		setMessage("5008");
	}else{
		ReleaseCapture();
		SetWaitCursor(false);
		st_Msg.Visible = false;
		alert(setPopMessage("1001")); //[알림] 정상적으로 처리가 완료되었습니다.
		setMessage("1001");
	}
}

