function common_OnChanged_SE(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "KE"){
		tableSelect = "FPW760PF";
	}else{
		tableSelect = "FPW860PF";
	}
	ds_FPQE150Q_1.ClearData();
	ds_FPQE150Q_4.ClearData();
	cmb_PRGGU.Enable = false;
}
