	     // 하기 mscomm속성을 스크립트로 작성하면 안됨: 원인불명
		//cmmB570.CommPort = 1;
		//cmmB570.Settings = "9600,n,8,1";
		//cmmB570.InputLen = 0;
		//cmmB570.DTREnable = True;

		cmmB570.PortOpen = true;
		//alert(cmmB570.PortOpen);
	}
}

function fncAxCommClose()
{       		
	cmmB570.PortOpen = false;
}
