}

function fn_print()
{
	if(confirm("선택된 ID LABEL 인쇄합니까? ") == false) return;

	//if(ds_FPQE150Q_4.rowcount <= 0 || ds_FPQE150Q_4.GetRowType(ds_FPQE150Q_4.row) != 'normal') return ;
	if(ds_FPQE150Q_4.rowcount <= 0) return ;
	if(!(ds_FPQE150Q_4.GetRowType(ds_FPQE140Q_4.row) == 'normal' || ds_FPQE150Q_4.GetRowType(ds_FPQE140Q_4.row) == 'update')) return ;

	ds_FPQE150Q_5.ClearData();
	for (var row = 0 ; row < ds_FPQE150Q_4.RowCount ; row++ ){   //선택된 것 만 ds_FPQE150Q_5에 ADD
		if(ds_FPQE150Q_4.GetSelect(row) == true){
			ds_FPQE150Q_5.addRow();
			ds_FPQE150Q_5.copyrow(ds_FPQE150Q_5.row,"ds_FPQE150Q_4",row);
		}
	}

	if(ds_FPQE150Q_5.rowcount <= 0 ){
		alert("선택후 발행.");
		return;
	}

	fncAxCommOpen();
	if(cmmB570.PortOpen == false){
		alert("Serial 통신을위한 COM1 Port오류.");
		return;
	}
/*--------------하기명령은 도시바SX5만 해당함으로 주석처리함...
	//status 요구 커멘드를 송신해 현재의 프린터 상태를 확인-------------------------start
	// cmmB570.Handshaking을 "comRTS"로   cmmB570.RTSEnable을 true로
	cmmB570.Output = ESC1 + "WS" + LF;
	cmmB570.Output = ext_VB_CHR(0);
	var sRcv;
	cmmB570.InputLen = 1;

	//sRcv = cmmB570.Input;
	for (var x=0; x < 100; x++){
		sRcv = cmmB570.Input;
		if (sRcv == chr(1)) break;
	}
	//sRcv = cmmB570.Input;
	for (var x=0; x < 100; x++){
		sRcv = cmmB570.Input;
		if (sRcv == chr(2)) break;
	}

	cmmB570.InputLen = 11;  //status 7byte+ ETX + EOT + CR + LF
	for (var x=0; x < 100; x++){
		sRcv = cmmB570.Input;
		if (sRcv != "") break;
	}
	var sRtnSts = left(sRcv, 7);
	cmmB570.InputLen = 0;
	if (left(sRtnSts, 2) != "00"){
		alert("프린터 준비가 되지않았습니다");
		return;
	}
	//status 요구 커멘드를 송신해 현재의 프린터 상태를 확인-------------------------end
*/

	BarSizePrint();  //LABEL SIZE정보및 LABEL프린터 특성
	var jQty = 0;
	var iQty = ds_FPQE150Q_5.RowCount;
	for (var i=0 ; i < iQty ; i++ ){
		FirstPrint(i);     //제일 처음의 한장  금회발행될 정보
		prtSTAT(i);		//인쇄정보 갱신
		jQty = toNumber(ds_FPQE150Q_5.GetColumn(i,"ORCQY"));  //발행총매수
		for (var j=0 ; j < jQty ; j++ ){
			siuFPW767PF("E1",ds_FPQE150Q_5.GetColumn(i,"PRTNO"),ds_FPQE150Q_5.GetColumn(i,"PNYMD"));   //label 발행번호
			var sqn = ds_FPQE150Q_767PF.GetColumn(0,"LSEQN");
			if (toNumber(sqn) > 999) sqn="1";
			ed_qtying.Text = j; // 인쇄정보 갱신
			ed_sqn.Text = right("000"+sqn,3);  // 인쇄정보 갱신
			BarDataPrint(i,j,sqn);
		}
		if(cb_Cutting.Value) BarCutting();
	}
	ed_prstat.Text = "인쇄완료";
	fncAxCommClose();
}

function prtSTAT(i)
{
	ed_prstat.Text = "인쇄중";
	ed_PROCS.Text = ds_FPQE150Q_5.GetColumn(i,"PROCS");
	ed_CUSNO.Text = ds_FPQE150Q_5.GetColumn(i,"CUSNO");
	ed_ORCQY.Text = ds_FPQE150Q_5.GetColumn(i,"ORCQY");
	ed_PRTNO.Text = ds_FPQE150Q_5.GetColumn(i,"PRTNO");
	ed_CPRTN.Text = ds_FPQE150Q_5.GetColumn(i,"CPRTN");
	ed_MKALC_CARCD.Text = ds_FPQE150Q_5.GetColumn(i,"MKALC")+" "+ds_FPQE150Q_5.GetColumn(i,"CARCD");
}
function siuFPW767PF(a,b,c)
{
	var comps = a;
	var prtno = b;
	var pnymd = c;

	var sKind		= "getFPQE150Q_767PF";						
	var sMethodName = "service::psfaclib/selectFPQE150Q_767PF.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var	sOutDataSet = "ds_FPQE150Q_767PF=ds_FPQE150Q_767PF";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(a); //사업부
		sArgument += " PRTNO=" + quote(b); //품번
		sArgument += " PNYMD=" + quote(c); //년월일
		if (tableSelect=="FPW760PF"){
			sArgument += " TABLE=" + quote("FPW767PF");  // DNKE사내용 ID label Seq No   
		}else{
			sArgument += " TABLE=" + quote("FPW867PF");  // 협력업체용(A Part) ID label Seq No  
		}
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync = false;
}
function BarSizePrint()
{
	Var sLabelSize, sLabelFeed, sSizeBuf, sLabelWR, sLabelAX, sLabelAY ;

	cmmB570.Output = ESC1 + "WR" + LF;
	cmmB570.Output = ext_VB_CHR(0);
	setSleepTime(5000);  // 5초 슬립
	switch (rdoggub.Value)
	{
		case "0" :
		cmmB570.Output = ESC1 + ("D") + ("0180,0400,0150") + LF;		cmmB570.Output = ext_VB_CHR(0);
		break;
		case "1" :
		cmmB570.Output = ESC1 + ("D") + ("0170,0450,0150") + LF;		cmmB570.Output = ext_VB_CHR(0);
		break;
		case "2" :
		cmmB570.Output = ESC1 + ("D") + ("0150,0450,0120") + LF;		cmmB570.Output = ext_VB_CHR(0);
		break;
	}

	cmmB570.Output = ESC1 + ("AX;") + ("-004,-004,+00") + LF;  //LABEL OR TAG 사이즈 결정
	cmmB570.Output = ext_VB_CHR(0);
	switch (rdoggub.Value)
	{
		case "0" :
		cmmB570.Output = ESC1 + ("AY") + (";+00,0") + LF;	cmmB570.Output = ext_VB_CHR(0);
		cmmB570.Output = ESC1 + ("T") + ("21C21") + LF;		cmmB570.Output = ext_VB_CHR(0);
		break;
		case "1" :
		cmmB570.Output = ESC1 + ("AY") + (";+00,0") + LF;	cmmB570.Output = ext_VB_CHR(0);
		cmmB570.Output = ESC1 + ("T") + ("21C21") + LF;		cmmB570.Output = ext_VB_CHR(0);
		break;
		case "2" :
		cmmB570.Output = ESC1 + ("AY") + (";-02,0") + LF;	cmmB570.Output = ext_VB_CHR(0);
		cmmB570.Output = ESC1 + ("T") + ("11C21") + LF;		cmmB570.Output = ext_VB_CHR(0);   //LABEL PRINTER의 특징설정
		break;
	}
}

