		cmmB570.Output = ESC1 + ("PC006;0005,0060,1,1,A,00,B") + LF;	cmmB570.Output = ext_VB_CHR(0);//업체품번
		cmmB570.Output = ESC1 + ("PC007;0345,0060,05,1,A,00,B") + LF;	cmmB570.Output = ext_VB_CHR(0);//ID LABEL유무
		break;
	}
	var tPRTNO = trim(ds_FPQE150Q_5.GetColumn(i,"PRTNO"));
	var tPNYMD = trim(ds_FPQE150Q_5.GetColumn(i,"PNYMD"));
	var tCARNM = trim(ds_FPQE150Q_5.GetColumn(i,"CARNM"));
	var tPROCS = trim(ds_FPQE150Q_5.GetColumn(i,"PROCS"));
	var tCUSNO = trim(ds_FPQE150Q_5.GetColumn(i,"CUSNO"));
	var tORCQY = trim(ds_FPQE150Q_5.GetColumn(i,"ORCQY"));
	var tCPRTN = trim(ds_FPQE150Q_5.GetColumn(i,"CPRTN"));
	var tPLPTNU = trim(ds_FPQE150Q_5.GetColumn(i,"PLPTNU"));

	cmmB570.Output = ESC1 + "RC000;"+tPRTNO + LF;	cmmB570.Output = ext_VB_CHR(0);
	cmmB570.Output = ESC1 + "RC001;"+tPNYMD + LF;	cmmB570.Output = ext_VB_CHR(0);
	cmmB570.Output = ESC1 + "RC002;"+tCARNM + LF;	cmmB570.Output = ext_VB_CHR(0);
	cmmB570.Output = ESC1 + "RC003;"+tPROCS + LF;	cmmB570.Output = ext_VB_CHR(0);
	cmmB570.Output = ESC1 + "RC004;"+tCUSNO + LF;	cmmB570.Output = ext_VB_CHR(0);
	cmmB570.Output = ESC1 + "RC005;Page:"+tORCQY + LF;	cmmB570.Output = ext_VB_CHR(0);
	cmmB570.Output = ESC1 + "RC006;"+tCPRTN + LF;	cmmB570.Output = ext_VB_CHR(0);
	cmmB570.Output = ESC1 + "RC007;Type:"+tPLPTNU + LF;	cmmB570.Output = ext_VB_CHR(0);
	switch (rdoggub.Value)
	{
		case "0" :
		cmmB570.Output = ESC1 + ("XS;I,0001,0002C2100") + LF;	cmmB570.Output = ext_VB_CHR(0);    //라벨 프린터 발행명령
		break;
		case "1" :
		cmmB570.Output = ESC1 + ("XS;I,0001,0002C2100") + LF;	cmmB570.Output = ext_VB_CHR(0);    //라벨 프린터 발행명령
		break;
		case "2" :
		cmmB570.Output = ESC1 + ("XS;I,0001,0001C2100") + LF;	cmmB570.Output = ext_VB_CHR(0);    //라벨 프린터 발행명령
		break;
	}
}

