			break;	
		case "M" : 
			// M TYPE의 특징은 상단 문자는 들어가지 않고 상단에 업체 ALC 코드의 39 BARCODE가 들어감
			//단 업체ALC는 하단부에도 인쇄되므로 하단부의 값을39 CODE로 이용
			cmmB570.Output = ESC1 + ("C") + LF;  cmmB570.Output = ext_VB_CHR(0); //BUFFER CLEAR
			cmmB570.Output = ESC1 + ("XB00;0020,0030,B,1,03,03,07,07,03,0,0065,+0000000000,0,00") + LF;    cmmB570.Output = ext_VB_CHR(0); //39 BARCODE FORMAT
			cmmB570.Output = ESC1 + ("XB01;0310,0025,T,L,04,A,0=") + qrData + LF;    cmmB570.Output = ext_VB_CHR(0);  //QR BARCODE FORMAT,X:0400,Y:=4.00MM,Y:=1.0MM
			//하기 출력할 문자값,좌표및 특성결정
			cmmB570.Output = ESC1 + "PC000;0005,0025,1,1,A,00,B" + LF;	cmmB570.Output = ext_VB_CHR(0);	//상단
			cmmB570.Output = ESC1 + "PC001;0090,0135,1,1,K,00,B" + LF;	cmmB570.Output = ext_VB_CHR(0);	//하단
			cmmB570.Output = ESC1 + "PC002;0340,0140,05,1,A,00,B" + LF;	cmmB570.Output = ext_VB_CHR(0);	//QR일련번호

			cmmB570.Output = ESC1 + "RB00;"+ substr(a028+space(4),0,4) + LF;	cmmB570.Output = ext_VB_CHR(0); //39 BARCODE값  

			//문자값MOVE
			cmmB570.Output = ESC1 + "RC000;"+a027 + LF;  cmmB570.Output = ext_VB_CHR(0);	//상단
			cmmB570.Output = ESC1 + "RC001;"+substr(a028+space(4),0,4) + LF;  cmmB570.Output = ext_VB_CHR(0);	//하단	
			cmmB570.Output = ESC1 + "RC002;"+qrData6 + LF;  cmmB570.Output = ext_VB_CHR(0); //QR일련번호
			break;
		case "Z" : //Test용 LABEL_Print_Z
			cmmB570.Output = ESC1 + ("C") + LF;  cmmB570.Output = ext_VB_CHR(0); //BUFFER CLEAR
			cmmB570.Output = ESC1 + ("XB00;0020,0030,B,1,03,03,07,07,03,0,0065,+0000000000,0,00") + LF;    cmmB570.Output = ext_VB_CHR(0); //39 BARCODE FORMAT
			cmmB570.Output = ESC1 + ("XB01;0310,0025,T,L,04,A,0=") + qrData + LF;    cmmB570.Output = ext_VB_CHR(0);  //QR BARCODE FORMAT,X:0400,Y:=4.00MM,Y:=1.0MM
			//하기 출력할 문자값,좌표및 특성결정
			cmmB570.Output = ESC1 + "PC000;0005,0025,1,1,A,00,B" + LF;	cmmB570.Output = ext_VB_CHR(0);	//상단
			cmmB570.Output = ESC1 + "PC001;0090,0135,1,1,K,00,B" + LF;	cmmB570.Output = ext_VB_CHR(0);	//하단
			cmmB570.Output = ESC1 + "PC002;0340,0140,05,1,A,00,B" + LF;	cmmB570.Output = ext_VB_CHR(0);	//QR일련번호

			cmmB570.Output = ESC1 + "RB00;"+ substr(a028+space(4),0,4) + LF;	cmmB570.Output = ext_VB_CHR(0); //39 BARCODE값  

			//문자값MOVE
			cmmB570.Output = ESC1 + "RC000;"+a002 + LF;  cmmB570.Output = ext_VB_CHR(0);	//상단
			cmmB570.Output = ESC1 + "RC001;"+substr(a028+space(4),0,4) + LF;  cmmB570.Output = ext_VB_CHR(0);	//하단	
			cmmB570.Output = ESC1 + "RC002;"+qrData6 + LF;  cmmB570.Output = ext_VB_CHR(0); //QR일련번호
			break;
		default :  //그외는 A Type 포멧으로 적용
			cmmB570.Output = ESC1 + ("C") + LF;  cmmB570.Output = ext_VB_CHR(0); //BUFFER CLEAR
			cmmB570.Output = ESC1 + ("XB01;0310,0025,T,L,04,A,0=") + qrData + LF;    cmmB570.Output = ext_VB_CHR(0);  //QR BARCODE FORMAT,X:0400,Y:=4.00MM,Y:=1.0MM
			//하기 출력할 문자값,좌표및 특성결정
			cmmB570.Output = ESC1 + "PC000;0010,0050,1,2,A,00,B" + LF;	cmmB570.Output = ext_VB_CHR(0);	//상단
			cmmB570.Output = ESC1 + "PC001;0010,0110,1,1,G,00,B" + LF;	cmmB570.Output = ext_VB_CHR(0);	//하단
			cmmB570.Output = ESC1 + "PC002;0340,0140,05,1,A,00,B" + LF;	cmmB570.Output = ext_VB_CHR(0);	//QR일련번호
			//문자값MOVE
			cmmB570.Output = ESC1 + "RC000;"+a027 + LF;  cmmB570.Output = ext_VB_CHR(0);	//상단
			cmmB570.Output = ESC1 + "RC001;"+a028 + LF;  cmmB570.Output = ext_VB_CHR(0);	//하단	
			cmmB570.Output = ESC1 + "RC002;"+qrData6 + LF;  cmmB570.Output = ext_VB_CHR(0); //QR일련번호
			break;
	}
	switch (rdoggub.Value)
	{
		case "0" :
		cmmB570.Output = ESC1 + ("XS;I,0001,0002C5100") + LF;	cmmB570.Output = ext_VB_CHR(0);    //라벨 프린터 발행명령
		break;
		case "1" :
		cmmB570.Output = ESC1 + ("XS;I,0001,0002C5100") + LF;	cmmB570.Output = ext_VB_CHR(0);    //라벨 프린터 발행명령
		break;
		case "2" :
		cmmB570.Output = ESC1 + ("XS;I,0001,0001C5100") + LF;	cmmB570.Output = ext_VB_CHR(0);    //라벨 프린터 발행명령
		break;
	}
}

function BarCutting()
{
	setSleepTime(500);  //0.5초 Sleep	
	cmmB570.InBufferCount = 0;
	cmmB570.OutBufferCount =0;
	cmmB570.Output = ESC1 + "C" + LF ;
	cmmB570.Output = ext_VB_CHR(0);
	cmmB570.Output = ESC1 + "XS;I,0001,0252C6100" + LF;
	cmmB570.Output = ext_VB_CHR(0);
}

