function getFPWE170(){

	if(!validationChk()){
		return;
	}

	var sKind		= "getFPWE170";						
	var sMethodName = "service::psfaclib/selectFPWE170.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FPWE170=ds_FPWE170";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@PSPNO=" + quote(ed_PSPNO.Text); //품번
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 

 //등록시 업체코드 존재유무 체크함. 없으면 동록불가.
function setFPWE170(){

	if(length(trim(ed_PSPNO.Text)) < 1){
		//alert("[알림]품번을 입력하세요.");
		alert(setPopMessage("2017"));  //[알림] 품번을 입력하세요.
		setMessage("2017");	
		ed_PSPNO.SetFocus();
		return;
	}
	if(ds_FPWE170.GetRowType(ds_FPWE170.currow)!="delete"){
		if(length(trim(ed_CPRNM.Text)) < 1){
			//alert("[알림]품명을 입력하세요.");
			alert(setPopMessage("6005"));  //[알림]품명을 입력하세요.
			setMessage("6005");	
			ed_CPRNM.SetFocus();
			return;
		}
		if(length(trim(ed_CUSNM.Text)) < 1){
			//alert("[알림]업체를 입력하세요.");
			alert(setPopMessage("4674"));  //[알림]업체를 입력하세요.
			setMessage("4674");	
			ed_CUSNM.SetFocus();
			return;
		}
		if(ed_PTCNT.Value < 1){
			//alert("[알림]용기수를 입력하세요.");
			alert(setPopMessage("6006"));  //[알림]용기수를 입력하세요.
			setMessage("6006");	
			ed_PTCNT.SetFocus();
			return;
		}
	}
	var sKind		= "setFPWE170";						
	var sMethodName = "service::psfaclib/saveFPWE170.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_FPWE170=ds_FPWE170:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FPWE170_CHECK=ds_FPWE170_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_FPWE170_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_FPWE170_CHECK.GetRowCount();i++){
			alert("품번 : " +ds_FPWE170_CHECK.GetColumn(i,"CPRTN") + " = " + ds_FPWE170_CHECK.GetColumn(i,"RTMSG"));
		}
		getFPWE170();  //error항목이 존재시에는 재조회
		return;
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		//getSalz101();  //error항목이 존재시에는 재조회
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	ed_PSPNO.BKColor="white";

	if(length(ed_PSPNO.Text)==0){
		ed_PSPNO.BKColor="paleturquoise";
		alert(setPopMessage("2017"));//[알림] 품번을 입력하세요.
		setMessage("2017");	
		result =false;
	}
	return result;
}

