 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "C-CAR바코드 마스터";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|C-CAR바코드 마스터";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

