function fn_Input()
{
	if(!validationChk()){
		return;
	}
	if(ds_FPWE170.rowcount > 0 ){
		//alert("[알림]등록은 1건만 가능합니다.");
		alert(setPopMessage("4747"));//[알림]등록은 1건만 가능합니다.
		setMessage("4747");
		return;
	}else{
		var sKind		= "getFPWE170";						
		var sMethodName = "service::psfaclib/selectFPWE170.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_FPWE170=ds_FPWE170";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " PSPNO=" + quote(ed_PSPNO.Text); //품번
			http.Sync=true;
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync=false;
		if(ds_FPWE170.rowcount>0){
			//alert("[경고] 이미 등록된 자료입니다.");
			ds_FPWE170.ClearData();
			alert(setPopMessage("1502"));//[경고] 이미 등록된 자료입니다.
			setMessage("1502");	
			return;
		}	
		else{
			var sKind		= "getEM000PR";						
			var sMethodName = "service::pscomlib/selectEM000PR.do";    // 서버에서 호출 될 Method 명
			var sInDataSet  = "";               //자료 저장 시 보낼 DataSet 명칭
			var sOutDataSet = "ds_EM000PR=ds_EM000PR";  	   // 조회 후 돌려 받을 DataSet 명칭
			var sArgument   = "";
				sArgument += " LIB1=" + quote(Lib1);
				sArgument += " CUSNO=" + quote(1900); //업체고정
				sArgument += " PRTNO=" + quote(ed_PSPNO.Text); //품번
			http.Sync=true;
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync=false;
			if(ds_EM000PR.rowcount<1){
				//alert("[확인] Part Number Cross Ref MA에 미 등록된 득의선,품번입니다.");
				alert(setPopMessage("4001"));//[알림] [확인] Part Number Cross Ref MA에 미 등록된 득의선,품번입니다.
				setMessage("4001");			
				return;
			}else{
				ds_FPWE170.AddRow();
				ds_FPWE170.SetColumn(ds_FPWE170.currow,"CPRTN",ed_PSPNO.Text);
				ds_FPWE170.SetColumn(ds_FPWE170.currow,"FATNM","JIANGSU");
				ds_FPWE170.SetColumn(ds_FPWE170.currow,"FATCD","K");
				ed_CPRNM.Text=trim(ds_EM000PR.GetColumn(0,"CPDSC"));
				ed_CUSNM.Text="DENSO POONGSUNG";
			}
		}		
	}	
}

function fn_Delete()
{
	if(ds_FPWE170.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_FPWE170.DeleteRow(ds_FPWE170.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
	setMessage("1011");
}

