function ds_FPWE600_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "PRCCD"){//일자
		if(length(varNewValue)!=0){
			var sKind		= "getBM001PR";						
			var sMethodName = "service::pscomlib/selectBM001PR.do";    // 서버에서 호출 될 Method 명
			var sInDataSet  = "";               //자료 저장 시 보낼 DataSet 명칭
			var sOutDataSet = "ds_BM001PR=ds_BM001PR";  	   // 조회 후 돌려 받을 DataSet 명칭
			var sArgument   = "";
				sArgument += " LIB1=" + quote(Lib1);
				sArgument += " PROCS=" + quote(varNewValue); //공정코드
			http.Sync=true;
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync=false;
			http.usewaitcursor = true;
			if(ds_BM001PR.rowcount<1){
				//alert("[알림] PROCESS MA에 미등록된 공정코드입니다.");
				alert(setPopMessage("4746"));//[알림] PROCESS MA에 미등록된 공정코드입니다.
				setMessage("4746");
				return false;		
			}
		}
	}	
}

function ds_FPWE600_CanRowPosChange(obj,nOldRow,nRow)
{
	http.usewaitcursor = false;

	if(ds_FPWE600.GetRowType(ds_FPWE600.currow)=="update" ||ds_FPWE600.GetRowType(ds_FPWE600.currow)=="insert" ){
		var p_PRCCD=ds_FPWE600.GetColumn(ds_FPWE600.currow,"PRCCD"); //공정
		var p_TAGDT=ds_FPWE600.GetColumn(ds_FPWE600.currow,"TAGDT"); //지시일
		var p_TAGQY=ds_FPWE600.GetColumn(ds_FPWE600.currow,"TAGQY"); //지시수량

		if(p_TAGQY !=0){
			if(length(p_PRCCD)==0){ //공정이 NULL인경우
				//alert("[알림] 공정코드를 입력하세요.");
				alert(setPopMessage("2026")); //[알림] 공정코드를 입력하세요.
				setMessage("2026");	
				return false;
			}
			if(length(p_TAGDT)==0){ //지시일이 NULL인 경우
				//alert("[알림]지시일자를 입력하세요.");
				alert(setPopMessage("6076")); //[알림]지시일자를 입력하세요.
				setMessage("6076");	
				return false;
			}
		}
		return true;
	}
	http.usewaitcursor = true;
}

function ds_FPWE600_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "TAGQY"){
		if(varNewValue==""){
			ds_FPWE600.SetColumn(nRow, "TAGQY", 0); // 지시수량
		}
	}
	http.usewaitcursor = true;
}
