var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("2.2"); //조회, 추가, 저장
	getComCode(); //공장구분 선택
	//cmb_PLNYM.Value="20080801";
	//ed_PRDCD.Text="T";
}

//수량,단가 경고메시지,
//mst100pf가져오는거 확인할것!
//
/* 확인 후 추가할것.
C*< 계획년월 CHECK >                                                 
C                   IF        (*IN44=*OFF AND *IN22=*ON)             
C                   MOVE      PLNYMK        PLNMM             2 0    
C*<JTWIM : 20051122 JTW 임시　수정사용 >                          2  
C*<CIGMA 달력은　２년뒤의　달력이　없는경우가　많음 >             2  
C*<DSPF  에서 RANGE 를　준뒤 PGM 에서는　월만　통제 >             2  
C*    KEY02         CHAIN     SC003F                             32  
C                   IF        (PLNMM>12 OR PLNMM<0)                  
C                   SETON                                        4344
C                   ENDIF                                            
C*  32              SETON                                        4344
C                   ENDIF                                            
*/
function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	//MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

	//Static0.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
	//Static3.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
}

function getFPYC200(){


	if(!validationChk()){
		return;
	}

	var sKind		= "getFPYC200";						
	var sMethodName = "service::psfaclib/selectFPYC200.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FPYC200=ds_FPYC200";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@PLNYM=" + quote(substr(cmb_PLNYM.Value,0,6)); //계획년월
		sArgument += "@PLNGB=" + quote(cmb_PLNGB.Value); //계획구분
		sArgument += "@PRDCD=" + quote(ed_PRDCD.Text); //제품코드
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/  
function setFPYC200(){

	if(!validationChk()){
		return;
	}
	if(ds_FPYC200.GetRowType(ds_FPYC200.currow)=="insert" ){
		if(!ds_FPYC200_CanRowPosChange()){
			return;
		}
	} 
	var sKind		= "setFPYC200";						
	var sMethodName = "service::psfaclib/saveFPYC200.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_FPYC200=ds_FPYC200:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FPYC200_CHECK=ds_FPYC200_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_FPYC200_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_FPYC200_CHECK.GetRowCount();i++){
			alert("품번 : " +ds_FPYC200_CHECK.GetColumn(i,"PSPNO") + " = " + ds_FPYC200_CHECK.GetColumn(i,"RTMSG"));
		}
		getFPYC200();  //error항목이 존재시에는 재조회
		return;
	}// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
		cmb_PLNYM.BKColor="white";
		cmb_PLNGB.BKColor="white";
		ed_PRDCD.BKColor="white";

	if (cmb_PLNYM.Value == ""){
		cmb_PLNYM.BKColor="paleturquoise";
		//alert("[알림]계획년월을 입력하세요.");
		alert(setPopMessage("6040"));//[알림]계획년월을 입력하세요.
		setMessage("6040");
		result = false;
	}else if(substr(cmb_PLNYM.Value,0,6) < 200604 || (substr(cmb_PLNYM.Value,0,6) > 200903)){
		cmb_PLNYM.BKColor="paleturquoise";
		//alert("[알림]계획년월은 200604~200903사이만 입력가능합니다.");
		alert(setPopMessage("6046"));//[알림]계획년월은 200604~200903사이만 입력가능합니다.
		setMessage("6046");
		result = false;
	}else if(cmb_PLNGB.Value==""){
		cmb_PLNGB.BKColor="paleturquoise";
		//alert("[알림]계획구분을 선택하세요.");
		alert(setPopMessage("6042"));//[알림]계획구분을 선택하세요.
		setMessage("6042");
		result = false;
	}else if(length(ed_PRDCD.Text)==0){
		ed_PRDCD.BKColor="paleturquoise";
		//alert("[알림]제품코드를 입력하세요.");
		alert(setPopMessage("6045"));//[알림]제품코드를 입력하세요.
		setMessage("6045");
		result = false;
	}
	return result;
}

