function fn_Input()
{
	if(!validationChk()){
		return;
	}

	if(checkInsertCount("ds_FPYC200")== false) {
		return;
	}
	if(ds_FPYC200.rowcount < 1) {
		fn_Search();
	}
	//rpg에서 제품코드는 등록일때만 mst050pf확인함.
	var sKind		= "getMST050";						
	var sMethodName = "service::pscomlib/selectMST050.do"; 	
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST050PF=ds_MST050PF";
	var sArgument   = "";
		sArgument   += " LIB2=" + quote(Lib2);
		sArgument   += " COMPS=" + quote(comps);
		sArgument   += " GRPCD='PRD'";
		sArgument   += " MSTCD=" + quote(ed_PRDCD.Text);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	if(ds_MST050PF.rowcount<1){
		//alert("[알림]제품코드를 확인하세요.");
		alert(setPopMessage("6047"));//[알림]제품코드를 확인하세요.
		setMessage("6047");		
		return;
	}
	ds_FPYC200.AddRow();
	ds_FPYC200.SetColumn(ds_FPYC200.currow,"COMPS",ComboBoxPlant.Value);
	ds_FPYC200.SetColumn(ds_FPYC200.currow,"PLNYM",substr(cmb_PLNYM.Value,0,6));
	ds_FPYC200.SetColumn(ds_FPYC200.currow,"PLNGB",cmb_PLNGB.Value);
	//ds_FPYC200.SetColumn(ds_FPYC200.currow,"PRDCD",ed_PRDCD.Text);
	ds_FPYC200.SetColumn(ds_FPYC200.currow,"MONQY",0);
	ds_FPYC200.SetColumn(ds_FPYC200.currow,"YERCS",0);
	ds_FPYC200.SetColumn(ds_FPYC200.currow,"MONAM",0);

	ds_FPYC200.SetColumn(ds_FPYC200.currow,"PYVND",0);//업체코드
	ds_FPYC200.SetColumn(ds_FPYC200.currow,"SHPTO",0);//SHPTO
	ds_FPYC200.SetColumn(ds_FPYC200.currow,"SPTEX",""); //특소세여부
	ds_FPYC200.SetColumn(ds_FPYC200.currow,"EXDSP",""); //내수구분
	ds_FPYC200.SetColumn(ds_FPYC200.currow,"YERQY",0); //년계획수량
	ds_FPYC200.SetColumn(ds_FPYC200.currow,"MRATE",0); //월계획율
}

function fn_Delete()
{

	if(ds_FPYC200.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_FPYC200.DeleteRow(ds_FPYC200.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
	setMessage("1011");
}

