function getOGMPrtno(tVal)
{
	http.usewaitcursor = false;
	var sKind		= "getOGMPrtno";		
	var sMethodName = "service::pscomlib/selectBM008PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_BM008PR=ds_BM008PR";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " ITNBR=" + quote(tVal);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	if(ds_BM008PR.rowcount < 1){
		//alert("[알림]ITEM MASTER A에 미등록된 품번입니다.");
		alert(setPopMessage("6032"));//[알림]ITEM MASTER A에 미등록된 품번입니다.
		setMessage("6032");
		return 0;	
	}
	else{
		if(ds_FPYC200.FindRow("PSPNO",tVal)!="-1"){
			alert(setPopMessage("4681"));//[경고]동일한 품번이 등록되어 있습니다.
			setMessage("4681");
			ds_FPYC200.SelectRow(ds_FPYC200.FindRow("PSPNO",tVal));
			ds_FPYC200.DeleteRow(ds_FPYC200.currow);
			return 0;
		}	
		//PROCESS CODE CHECK
		var sKind		= "getBM002PR";		
		var sMethodName = "service::psfaclib/selectBM002PR.do";    // 서버에서 호출 될 Method 명
		var sInDataSet  = "";
		var sOutDataSet = "ds_BM002PR=ds_BM002PR";
		var sArgument   = "";
			sArgument += " LIB1=" + quote(Lib1);
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument += " PRTNO=" + quote(tVal); //품번
			sArgument += " FLAG=" + quote("PRTNO");
		http.Sync=true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync=false;
		if (ds_BM002PR.rowcount < 1){
			//alert("[알림]Item & Process MA에 미등록된 품번입니다");
			alert(setPopMessage("6035"));//[알림]Item & Process MA에 미등록된 품번입니다
			setMessage("6035");	
			return 0;
		}else{
			//4.차종코드조회
			var sKind		= "getMST100PF";		
			var sMethodName = "service::pscomlib/selectMST100PF.do"; 
			var sInDataSet  = "";
			var sOutDataSet = "ds_MST100PF=ds_MST100PF";
			var sArgument   = "";
				sArgument  += " LIB2=" + quote(Lib2);
				sArgument  += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
				sArgument  += " PRTNO=" + quote(tVal); //품번
			http.Sync=true;
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync=false;
		}
	}
	http.usewaitcursor = true;

}

function ds_FPYC200_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "PSPNO"){
		if(varNewValue != "" ){
			ds_FPYC200.SetColumn(nRow,"ITDSC",ds_BM008PR.GetColumn(0,"ITDSC"));//품명
			//ds_FPYC200.SetColumn(nRow,"PYVND",0);//업체코드
			//ds_FPYC200.SetColumn(nRow,"SHPTO",0);//SHPTO
			ds_FPYC200.SetColumn(nRow,"PLTCD",ds_BM008PR.GetColumn(0,"PACKC"));//공장코드
			ds_FPYC200.SetColumn(nRow,"PROCS",ds_BM002PR.GetColumn(0,"PROCS"));//공정코드
			//ds_FPYC200.SetColumn(nRow,"SPTEX",""); //특소세여부
			//ds_FPYC200.SetColumn(nRow,"EXDSP",""); //내수구분
			//ds_FPYC200.SetColumn(nRow,"YERQY",0); //년계획수량
			//ds_FPYC200.SetColumn(nRow,"MRATE",0); //월계획율
			ds_FPYC200.SetColumn(nRow,"PRDCD",ds_MST100PF.GetColumn(0,"PRDCD"));//제품코드
			ds_FPYC200.SetColumn(nRow,"CARCD",ds_MST100PF.GetColumn(0,"CARCD"));//차종코드
			ds_FPYC200.SetColumn(nRow,"BDYCD",ds_MST100PF.GetColumn(0,"BDYCD"));//BDY코드
		}else{
			ds_FPYC200.SetColumn(nRow,"ITDSC","");
			//ds_FPYC200.SetColumn(nRow,"PYVND",0);//업체코드
			//ds_FPYC200.SetColumn(nRow,"SHPTO",0);//SHPTO
			ds_FPYC200.SetColumn(nRow,"PLTCD","");//공장코드
			ds_FPYC200.SetColumn(nRow,"PROCS","");//공정코드
			//ds_FPYC200.SetColumn(nRow,"SPTEX",""); //특소세여부
			//ds_FPYC200.SetColumn(nRow,"EXDSP",""); //내수구분
			//ds_FPYC200.SetColumn(nRow,"YERQY",0); //년계획수량
			//ds_FPYC200.SetColumn(nRow,"MRATE",0); //월계획율
		}
	}
	if(strColumnID == "MONQY"){
		if(varNewValue == "" ){
			ds_FPYC200.SetColumn(nRow,"MONQY",0);//수량
		}
		var monqy = toNumber(ds_FPYC200.GetColumn(nRow,"MONQY"));
		var yercs = toNumber(ds_FPYC200.GetColumn(nRow,"YERCS"));	
		ds_FPYC200.SetColumn(nRow,"MONAM", (monqy*yercs));
	}
	if(strColumnID == "YERCS"){
		if(varNewValue == "" ){
			ds_FPYC200.SetColumn(nRow,"YERCS",0);//단가
		}
		var monqy = toNumber(ds_FPYC200.GetColumn(nRow,"MONQY"));
		var yercs = toNumber(ds_FPYC200.GetColumn(nRow,"YERCS"));	
		ds_FPYC200.SetColumn(nRow,"MONAM", (monqy*yercs));
	}
}

function ds_FPYC200_CanRowPosChange(obj,nOldRow,nRow)
{
	//if(ds_FPYC200.GetRowType(ds_FPYC200.currow)=="update" ||ds_FPYC200.GetRowType(ds_FPYC200.currow)=="insert" ){
	if(ds_FPYC200.GetRowType(ds_FPYC200.currow)=="insert" ){ //RPG업데이트시에만 체크함.
		var pspno=ds_FPYC200.GetColumn(ds_FPYC200.currow,"PSPNO");
		var monqy=ds_FPYC200.GetColumn(ds_FPYC200.currow,"MONQY");
		var yercs=ds_FPYC200.GetColumn(ds_FPYC200.currow,"YERCS");

		if(length(pspno)!=0){
			if(monqy<1){	
				alert(setPopMessage("6038"));  // [알림]수량은 0보다 커야합니다.
				setMessage("6038");	
				//alert("[알림]수량은 0보다 커야합니다.");
				return false;
			}
			else if(yercs<1){
				alert(setPopMessage("6039"));  // [알림]단가는 0보다 커야합니다.
				setMessage("6039");	
				alert("[알림]단가는 0보다 커야합니다.");
				return false;			
			}
		}
		return true;	
	}	
}

function ed_PRDCD_OnCharChanged(obj,strPreText,strPostText)
{
	//st_PRDCD.Text = "";
	ds_FPYC200.ClearData();
}

function ed_PRDCD_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getMST050PF";						
	var sMethodName = "service::pscomlib/selectMST050PF.do"; 
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MST050PF=ds_MST050PF";    	// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " GRPCD=" + quote("PRD"); //GRP CODE
		sArgument += " MSTCD=" + quote(ed_PRDCD.Text);//PRD CODE
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_MST050PF.rowcount < 1){
		alert(setPopMessage("6047"));//[알림]제품코드를 확인하세요.
		setMessage("6047");	
		ed_PRDCD.SetFocus();
		return;
	}
	//st_PRDCD.Text = ds_MST050PF.GetColumn(0,"MSTRM");
	http.usewaitcursor = true;
}
function ed_PRDCD_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		fn_Search();
	}	
}

